INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_958', 'gestionnaire de flux reverse logistic', '<p>Le ou la gestionnaire de flux reverse logistic (logistique inversée) optimise et gère le transport, le stockage et le traitement des produits retournés par les clients. Un canapé, un vêtement, un appareil ménager... peut être réparé, reconditionné, recyclé, revendu.</p>', '[{"nom": "gestionnaire de flux reverse logistic", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.958"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_952', 'aide-chimiste', '<p>L''aide-chimiste concourt aux expériences et analyses réalisées dans les laboratoires industriels, participant ainsi à l''élaboration de nouvelles molécules, composants ou produits. Il intervient aussi lors des opérations de contrôle et de qualité.</p>', '[{"nom": "aide-chimiste", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.952"}, {"nom": "Intervention technique en laboratoire d''analyse industrielle", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1503"}]');
