INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_962', 'orthodontiste', '<p>Docteur en chirurgie dentaire, l''orthodontiste reçoit enfants, adolescents, mais aussi adultes pour corriger l''alignement de leurs dents ou la position de leur mâchoire. Il pose bagues et appareils qu''il ajuste régulièrement avec minutie. </p>', '[{"nom": "Médecine dentaire", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=J1103"}, {"nom": "orthodontiste", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.962"}]');
