INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_965', 'ouvrier / ouvrière plasturgiste', '<p>Le plastique est une matière dérivée principalement du pétrole. L''ouvrier plasturgiste travaille sur des machines transformant des billes ou des granulés de plastique en toutes sortes d''objets du quotidien : chaises, coques de téléphones, etc.</p>', '[{"nom": "ouvrier / ouvrière plasturgiste", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.965"}, {"nom": "Conduite d''équipement de formage des plastiques et caoutchoucs", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H3201"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_964', 'éducateur canin / éducatrice canine', '<p>Ne pas tirer sur la laisse, rester assis, venir à l''appel, ne pas mordre... voici quelques-uns des comportements que l''éducateur canin inculque aux chiens non disciplinés.</p>', '[{"nom": "Élevage d''animaux sauvages ou de compagnie", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=A1408"}, {"nom": "éducateur canin / éducatrice canine", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.964"}]');
