INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_935', 'ingénieur / ingénieure recherche et développement (R&D) en agroéquipement', '<p>L''ingénieur recherche et développement (R&amp;D) en agroéquipement imagine le tracteur et les machines de demain, les drones ou les robots qui assisteront les exploitants agricoles ou forestiers, sans perdre de vue le rendement et le respect de l''environnement.</p>', '[{"nom": "ingénieur / ingénieure recherche et développement (R&D) en agroéquipement", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.935"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_937', 'enseignant / enseignante en activité physique adaptée', '<p>Spécialiste de l''activité physique, de la santé et du handicap, l''enseignant en activité physique adaptée (Apa) intervient auprès de personnes dont les aptitudes physiques, psychologiques ou les conditions sociales réduisent l''activité physique.</p>', '[{"nom": "Éducation en activités sportives", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=G1204"}, {"nom": "enseignant / enseignante en activité physique adaptée", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.937"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_939', 'art-thérapeute', '<p>Intégré à l''équipe paramédicale, l''art-thérapeute est un spécialiste de l''activité artistique qui exploite le pouvoir et les effets de l''art dans une visée thérapeutique et humanitaire pour soulager les personnes souffrant de déficits physiques, psychiques ou socio-relationnels.</p>', '[{"nom": "Psychologie", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K1104"}, {"nom": "art-thérapeute", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.939"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_930', 'programmiste', '<p>Grâce à son étude minutieuse en amont de la construction, le programmiste assiste le maître d''ouvrage, lui permettant d''éviter toute dérive ou contentieux plus tard. Coûts, recherche de contraintes, utilisation optimale... rien ne lui échappe.</p>', '[{"nom": "programmiste", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.930"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_932', 'statisticien/ne en géomarketing', '<p>À la croisée des statistiques, du marketing et de l''information géographique, le statisticien en géomarketing utilise ces données pour aider son client à choisir une localisation, optimiser l''implantation de ses points de vente ou améliorer ses services.</p>', '[{"nom": "statisticien / statisticienne en géomarketing", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.932"}, {"nom": "Études et prospectives socio-économiques", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=M1403"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_931', 'ingénieur mathématicien / ingénieure mathématicienne', '<p>L''ingénieur mathématicien utilise ses connaissances théoriques pour apporter des solutions concrètes à des problématiques complexes, dans tous les secteurs d''activité. Ses travaux apportent des éléments clés pour le développement industriel notamment.</p>', '[{"nom": "Recherche en sciences de l''univers, de la matière et du vivant", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K2402"}, {"nom": "ingénieur mathématicien / ingénieure mathématicienne", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.931"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_934', 'chef / cheffe d''exploitation des remontées mécaniques', '<p>Le chef d''exploitation des remontées mécaniques a la responsabilité d''acheminer les clients sur les domaines skiables. Il coordonne les opérations de mise en fonction, de conduite et de surveillance des téléskis ou des téléphériques.</p>', '[{"nom": "chef / cheffe d''exploitation des remontées mécaniques", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.934"}, {"nom": "Direction et ingénierie en entretien infrastructure et bâti", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=I1101"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_933', 'ingénieur/e d''affaires en génie électrique', '<p>L''ingénieur d''affaires en génie électrique élabore le dossier technique d''un réseau électrique, que ce dernier soit à installer ou à moderniser. Débouchés dans toutes les industries de pointe et dans les grandes entreprises comme EDF, la SNCF ou la RATP.</p>', '[{"nom": "ingénieur / ingénieure d''affaires en génie électrique", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.933"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_947', 'ingénieur/e structures', '<p>À partir des plans de l''architecte, l''ingénieur structures calcule les dimensions des murs, poutres, etc. pour assurer la stabilité d''un ouvrage. Pour cela, il réalise des plans en 3D et des simulations pour tester ses calculs.</p>', '[{"nom": "ingénieur / ingénieure structures", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.947"}, {"nom": "Ingénierie et études du BTP", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=F1106"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_902', 'BIM manager', '<p>La modélisation des données a bouleversé le secteur du BTP (bâtiment et travaux publics). Le ou la BIM manager est la tête de pont de ce mouvement visant à optimiser les échanges entre les professionnels, limiter les erreurs et donc gagner en temps et en qualité.</p>', '[{"nom": "BIM manager", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.902"}, {"nom": "Ingénierie et études du BTP", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=F1106"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_946', 'responsable de laboratoire de contrôle en biologie', '<p>Sous la direction du responsable du laboratoire de contrôle, des tests sont effectués pour évaluer la qualité et la conformité d''un médicament ou d''un produit cosmétique avant sa mise en place sur le marché... ou l''arrêt de sa production.</p>', '[{"nom": "Direction de laboratoire d''analyse industrielle", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1501"}, {"nom": "responsable de laboratoire de contrôle en biologie", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.946"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_949', 'ingénieur/e concepteur/trice en mécanique', '<p>Acteur central du service R &amp; D (recherche et développement), l''ingénieur concepteur en mécanique imagine la forme des produits du futur (voitures électriques, éoliennes, robots...) en fonction des matériaux choisis. Ses missions sont stratégiques et, bien souvent, confidentielles.</p>', '[{"nom": "Management et ingénierie de production", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H2502"}, {"nom": "ingénieur concepteur / ingénieure conceptrice en mécanique", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.949"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_948', 'mécatronicien/ne', '<p>Au carrefour de la mécanique, de l''électronique et de l''informatique, le mécatronicien crée des ensembles automatisés miniaturisés. Les applications sont très nombreuses et les secteurs qui recrutent variés, de l''automobile à la défense.</p>', '[{"nom": "Management et ingénierie études, recherche et développement industriel", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1206"}, {"nom": "mécatronicien / mécatronicienne", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.948"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_941', 'guide-conférencier / guide-conférencière', '<p>Dans un musée ou lors d''un circuit dans un pays étranger, le guide-conférencier partage ses vastes connaissances avec un groupe de touristes. Il est capable de s''exprimer en plusieurs langues et travaille généralement en indépendant.</p>', '[{"nom": "guide-conférencier / guide-conférencière", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.941"}, {"nom": "Accompagnement de voyages, d''activités culturelles ou sportives", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=G1201"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_940', 'ingénieur/e essais sol sur aéronef', null, '[]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_943', 'ingénieur / ingénieure en construction automobile', '<p>Passionné par l''automobile et la mécanique, l''ingénieur en construction automobile participe à l''amélioration des modèles existants ou à la conception des véhicules du futur. Révolution écologique et évolutions technologiques l''obligent à innover.</p>', '[{"nom": "Management et ingénierie études, recherche et développement industriel", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1206"}, {"nom": "ingénieur / ingénieure en construction automobile", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.943"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_942', 'technicien / technicienne en traitement des matériaux', '<p>Dans l''industrie, certains matériaux (métal, plastique, verre, céramique...) doivent subir des traitements pour être plus résistants, plus souples etc. Le technicien en traitement des matériaux contrôle ces opérations de traitement chimique, mécanique ou thermique.</p>', '[{"nom": "technicien / technicienne en traitement des matériaux", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.942"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_945', 'consultant / consultante green IT', '<p>Alléger la consommation d''énergie des ordinateurs ou d''un centre téléphonique, optimiser la durée de vie du matériel, réduire les déchets... telles sont les missions du consultant green IT, qu''il soit intégré à l''entreprise ou consultant extérieur.</p>', '[{"nom": "Conseil en organisation et management d''entreprise", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=M1402"}, {"nom": "consultant / consultante green IT", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.945"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_944', 'chef / cheffe de projet biodiversité', '<p>La cheffe ou le chef de projet biodiversité cherche à minimiser l''impact négatif de l''activité humaine sur le milieu naturel lors d''un projet d''aménagement. Ses objectifs? Préserver au maximum la faune et la flore, et compenser ce qui est détruit.</p>', '[{"nom": "Ingénierie en agriculture et environnement naturel", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=A1303"}, {"nom": "chef / cheffe de projet biodiversité", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.944"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_916', 'consultant/e en informatique décisionnelle', '<p>Spécialiste des bases de données, le consultant en informatique décisionnelle propose et met en place des solutions informatiques pour les dirigeants des entreprises qui le sollicitent. Sa ligne de mire : améliorer les performances de leur entreprise.</p>', '[{"nom": "Conseil et maîtrise d''ouvrage en systèmes d''information", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=M1806"}, {"nom": "consultant / consultante en informatique décisionnelle", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.916"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_915', 'charpentier/ère de marine', null, '[]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_918', 'guide de haute montagne', '<p>Cordes, mousquetons, crampons, piolets... tels sont les outils du guide de haute montagne pour faire découvrir à des touristes les sommets, en toute sécurité, en hiver mais aussi en été, puisqu''il est habilité à encadrer de nombreuses activités de haute montagne.</p>', '[{"nom": "guide de haute montagne", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.918"}, {"nom": "Accompagnement de voyages, d''activités culturelles ou sportives", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=G1201"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_917', 'directeur / directrice de création', '<p>Valoriser l''image d''une entreprise, manager une équipe de créatifs, développer la créativité... Le quotidien du directeur de création est ponctué d''idées lumineuses, de visuels attractifs et de textes accrocheurs. Sa passion : la communication !</p>', '[{"nom": "Développement et promotion publicitaire", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=E1401"}, {"nom": "directeur / directrice de création", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.917"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_919', 'agent / agente de développement touristique', '<p>L''agent de développement touristique met sur pied une stratégie globale pour faire connaître ou valoriser un lieu, une commune, une région, etc. En lien avec de nombreux partenaires, il suit la mise en place du projet et supervise la communication.</p>', '[{"nom": "agent / agente de développement touristique", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.919"}, {"nom": "Promotion du tourisme local", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=G1102"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_1111', 'courtier/ère en immobilier', null, '[]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_1110', 'courtier/ère en assurances', null, '[]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_910', 'diagnostiqueur / diagnostiqueuse immobilier', '<p>Le diagnostiqueur immobilier intervient avant tout achat ou location d''un logement pour en déterminer l''état. Plomb, amiante, électricité, gaz, termites, performance énergétique... la loi prévoit une série de contrôles qui ne s''improvisent pas.</p>', '[{"nom": "diagnostiqueur / diagnostiqueuse immobilier", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.910"}, {"nom": "Contrôle et diagnostic technique du bâtiment", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=F1103"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_912', 'hydrologue', '<p>Spécialiste de l''étude du cycle de l''eau, l''hydrologue contrôle la qualité et les quantités d''eau de surface, depuis la source jusqu''à la distribution. L''eau étant désormais un enjeu stratégique, les hydrologues sont de plus en plus sollicités.</p>', '[{"nom": "hydrologue", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.912"}, {"nom": "Études géologiques", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=F1105"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_911', 'ingénieur biomédical / ingénieure biomédicale', '<p> L''ingénieur biomédical est un spécialiste des matériels et des équipements de haute technologie destinés au secteur hospitalier, aux établissements de soins et aux professionnels de la santé. Il supervise une équipe de techniciens biomédicaux. </p>', '[{"nom": "Management et ingénierie études, recherche et développement industriel", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1206"}, {"nom": "ingénieur biomédical / ingénieure biomédicale", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.911"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_925', 'courtier / courtière', '<p>Particuliers et entreprises comptent sur le courtier pour dénicher la meilleure offre au meilleur prix. Une prouesse qu''il ne peut accomplir sans une bonne connaissance du marché et des talents de négociateur.</p>', '[{"nom": "courtier / courtière", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.925"}, {"nom": "Courtage en assurances", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=C1103"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_924', 'éducateur / éducatrice de la protection judiciaire de la jeunesse', '<p>L''éducateur de la PJJ (protection judiciaire de la jeunesse) intervient auprès de mineurs en conflit avec la loi, délinquants ou en danger. Qu''il partage leur quotidien ou les rencontre régulièrement, il leur apporte une aide éducative et favorise leur réinsertion sociale.</p>', '[{"nom": "Intervention socioéducative", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K1207"}, {"nom": "éducateur / éducatrice de la protection judiciaire de la jeunesse", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.924"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_927', 'responsable de site de traitement des déchets', '<p>Avec 340 millions de tonnes de déchets produits par an par les ménages et les entreprises, le ou la responsable de site de traitement des déchets a fort à faire ! Sa mission : gérer les opérations de recyclage, d''incinération, de valorisation et d''enfouissement.</p>', '[{"nom": "responsable de site de traitement des déchets", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.927"}, {"nom": "Supervision d''exploitation éco-industrielle", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K2306"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_929', 'paléontologue', '<p> Fouiller le passé, rechercher et étudier des créatures préhistoriques, des os ou des végétaux fossilisés vieux de plusieurs millions d''années... tel est le travail du paléontologue, un scientifique popularisé par le film " Jurassic Park ". </p>', '[{"nom": "paléontologue", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.929"}, {"nom": "Recherche en sciences de l''homme et de la société", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K2401"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_928', 'designer d''interaction', '<p>Le designer d''interaction choisit la façon dont un objet ou un service va se comporter dans le dialogue avec l''utilisateur. Les applications de son métier sont très nombreuses, du secteur des transports à celui de la santé en passant par la culture.</p>', '[{"nom": "designer d''interaction", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.928"}, {"nom": "Réalisation de contenus multimédias", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=E1205"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_921', 'anatomiste', '<p>Chercheur en médecine, l''anatomiste est un spécialiste de la structure, de la composition, de la croissance et du fonctionnement des organes et tissus du corps humain ou animal.</p>', '[{"nom": "anatomiste", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.921"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_920', 'ingénieur/e maintenance aéronautique', '<p>Dans le secteur aéronautique, l''ingénieur maintenance est responsable de l''entretien des avions et des hélicoptères (civils ou militaires), mais aussi des lanceurs spatiaux, des satellites et des missiles. Il dirige des équipes de techniciens.</p>', '[{"nom": "ingénieur / ingénieure maintenance aéronautique", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.920"}, {"nom": "Management et ingénierie de maintenance industrielle", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=I1102"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_923', 'développeur / développeuse économique', '<p>Pour favoriser l''essor d''une ville, d''une agglomération ou d''une zone rurale, le développeur économique stimule la création d''entreprise ou accompagne les sociétés déjà implantées dans leurs projets.</p>', '[{"nom": "développeur / développeuse économique", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.923"}, {"nom": "Développement local", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K1802"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_922', 'agent / agente de développement local', '<p>L''agente ou l''agent de développement local fait en sorte que les habitants d''un quartier, d''une ville ou d''une zone rurale vivent mieux ensemble. Mi-animateurs mi-médiateurs, ces professionnels dynamisent le territoire sur le plan socioculturel.</p>', '[{"nom": "agent / agente de développement local", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.922"}, {"nom": "Développement local", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K1802"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_903', 'ouvrier / ouvrière agricole', '<p>Bras droit de l''agriculteur, l''ouvrier agricole participe à tous les travaux d''une exploitation. C''est une occupation pointue et un bon marchepied pour s''installer ensuite à son compte.</p>', '[{"nom": "Polyculture, élevage", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=A1416"}, {"nom": "ouvrier / ouvrière agricole", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.903"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_1113', 'courtier/ère en prêt immobilier', null, '[]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_1112', 'courtier/ère en marchandises', null, '[]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_1115', 'ethnolinguiste', null, '[]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_1114', 'courtier/ère en travaux', null, '[]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_905', 'soigneur / soigneuse d''animaux', '<p>S''occuper des lions, des éléphants, des dauphins : le rêve de nombreux enfants, mais le métier d''une petite poignée d''adultes. Au quotidien, le soigneur est au service d''animaux exotiques ou familiers, sauvages ou apprivoisés.</p>', '[{"nom": "Aide aux soins animaux", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=A1501"}, {"nom": "soigneur / soigneuse d''animaux", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.905"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_904', 'constructeur / constructrice de routes et d''aménagements urbains', '<p>Le constructeur ou la constructrice de routes et d''aménagements urbains construit, entretient et modernise les infrastructures de circulation (routes, rues, voies piétonnes, pistes cyclables, équipements publics et mobiliers urbains, végétalisation des espaces urbains, aires de stationnement, parkings, plateformes logistiques, installations de loisirs et d''équipements sportifs, ralentisseurs, aérodromes, pistes d''aéroport, etc.).</p>', '[{"nom": "Construction de routes et voies", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=F1702"}, {"nom": "constructeur / constructrice de routes et d''aménagements urbains", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.904"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_907', 'conducteur / conductrice de bus ou d''autocar', '<p>Sécurité, ponctualité et qualité de service sont les préoccupations premières du conducteur de bus. Cet amoureux de la conduite n''assure pas seulement le transport : il accueille, informe et conseille les usagers.</p>', '[{"nom": "Conduite de transport en commun sur route", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=N4103"}, {"nom": "conducteur / conductrice de bus ou d''autocar", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.907"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_906', 'responsable de la collecte des déchets ménagers', '<p>À la tête d''une équipe d''agents de propreté urbaine, le ou la responsable de la collecte des déchets ménagers coordonne les opérations de ramassage et de tri dans sa municipalité. Son rôle : orchestrer la ronde matinale des bennes d''enlèvement des ordures.</p>', '[{"nom": "responsable de la collecte des déchets ménagers", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.906"}, {"nom": "Management et inspection en environnement urbain", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K2302"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_909', 'conseiller / conseillère en assurances', '<p>Il gère un portefeuille de clients, auxquels il propose et vend des contrats d''assurances (habitation, automobile, épargne...). Prospecter une nouvelle clientèle sur une zone géographique précise, et la fidéliser font aussi partie de ses missions.</p>', '[{"nom": "conseiller / conseillère en assurances", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.909"}, {"nom": "Conseil clientèle en assurances", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=C1102"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_908', 'hydraulicien/ne', '<p>Spécialiste de la mécanique des fluides, l''hydraulicien ou l''hydraulicienne intervient dans la conception ou la gestion de centrales hydroélectriques, de systèmes d''assainissement ou de réseaux d''irrigation et d''alimentation en eau potable.</p>', '[{"nom": "hydraulicien / hydraulicienne", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.908"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_901', 'rédacteur / rédactrice en chef', '<p>Presse, radio, télévision, Web... tous les médias ont besoin d''un rédacteur en chef qui donne le ton et définit les contenus. Ce professionnel de l''information anime l''équipe de journalistes de sa rédaction et développe l''image du support pour lequel il travaille.</p>', '[{"nom": "Journalisme et information média", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=E1106"}, {"nom": "rédacteur / rédactrice en chef", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.901"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_900', 'élagueur / élagueuse', '<p>L''élagueur travaille en hauteur, dans les arbres, à bord d''une nacelle ou arnaché avec un harnais. Il taille, souvent à la tronçonneuse électrique, des branches mortes ou malades, en binôme avec un collègue au sol, dans le respect des règles de sécurité.</p>', '[{"nom": "élagueur / élagueuse", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.900"}, {"nom": "Bûcheronnage et élagage", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=A1201"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_891', 'secrétaire administratif/ve', e'Rédaction, communication, comptabilité, encadrement et gestion du personnel : la palette des attributions d\'un/e secrétaire administratif/ve est très large. Elle dépend beaucoup de son lieu d\'affectation : commune, université, ministère...<h3>Après la 3<sup>e</sup></h3>
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_893', 'agent / agente de développement des énergies renouvelables', '<p>Intégrer la dimension énergétique dans les politiques locales : c''est la mission de l''agent de développement des énergies renouvelables, à la fois animateur, technicien et gestionnaire. Gros plan sur ce métier, au service de la ville et au coeur de l''enjeu environnemental.</p>', '[{"nom": "agent / agente de développement des énergies renouvelables", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.893"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_892', 'technicien/ne de police technique et scientifique', e'Empreintes génétiques, études de faux documents, examens de peintures, d\'explosifs, d\'armes... La technicienne ou le technicien de police technique et scientifique cherche à identifier les auteurs d\'infractions. Il ou elle contribue à la lutte contre le terrorisme, le crime organisé ou la délinquance.<h3>Après le bac</h3>
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_895', 'technicien / technicienne d''intervention clientèle gaz', '<p>Le technicien d''intervention clientèle gaz entretient et répare les installations d''alimentation en gaz chez des clients. Son activité repose sur 3 missions essentielles : les interventions techniques, la maintenance et le conseil.</p>', '[{"nom": "technicien / technicienne d''intervention clientèle gaz", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.895"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_894', 'ingénieur / ingénieure en énergie solaire', '<p>De l''abri solaire qui recharge un véhicule électrique à la centrale qui produit de l''électricité, l''ingénieur en énergie solaire conçoit et pilote des projets au coeur de l''énergie dite verte.</p>', '[{"nom": "ingénieur / ingénieure en énergie solaire", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.894"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_897', 'modiste', '<p>Le modiste imagine et réalise sur mesure, à l''unité ou en petites séries, des chapeaux et accessoires de tête, qu''il vend ensuite dans sa boutique à une clientèle de particuliers ou aux professionnels du monde du spectacle ou de la mode.</p>', '[{"nom": "Réalisation d''articles de chapellerie", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=B1801"}, {"nom": "modiste", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.897"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_896', 'technicien / technicienne de la circulation ferroviaire SNCF', '<p>Le technicien de la circulation ferroviaire SNCF, prépare, organise et gère l''incessante circulation des trains dans une zone géographique donnée, depuis le poste d''aiguillage ou le centre de régulation du trafic.</p>', '[{"nom": "technicien / technicienne de la circulation ferroviaire SNCF", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.896"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_899', 'soudeur / soudeuse', '<p>Le soudeur assemble, par fusion ou par apport de métal, les différents éléments qui composent un chauffe-eau, un avion, un pont de plate-forme...</p>', '[{"nom": "soudeur / soudeuse", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.899"}, {"nom": "Soudage manuel", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H2913"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_898', 'chargé / chargée d''études ressources humaines', '<p>Le chargé d''études ressources humaines (RH) harmonise les pratiques RH au sein d''un groupe : il pointe les dysfonctionnements et propose des solutions. Ce professionnel est souvent un jeune diplômé qui fait ses premiers pas dans les RH.</p>', '[{"nom": "chargé / chargée d''études ressources humaines", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.898"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_871', 'psychologue', '<p>Qu''il s''agisse d''accompagner des malades, de dépister des troubles du comportement ou de recruter du personnel..., le psychologue écoute, observe, évalue et conseille.</p>', '[{"nom": "psychologue", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.871"}, {"nom": "Psychologie", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K1104"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_870', 'chirurgien-dentiste / chirurgienne-dentiste', '<p>Du détartrage à la pose d''implants, en passant par l''extraction d''une dent de sagesse, le chirurgien-dentiste fait jouer sa dextérité, doublée de la maîtrise de techniques de soins complexes.</p>', '[{"nom": "Médecine dentaire", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=J1103"}, {"nom": "chirurgien-dentiste / chirurgienne-dentiste", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.870"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_873', 'technicien thermicien / technicienne thermicienne', '<p>Le technicien thermicien est un spécialiste des installations de chauffage, de production d''énergie et de climatisation. Il contrôle, régule, installe et assure la maintenance d''équipements chez les clients en respectant les normes environnementales.</p>', '[{"nom": "technicien thermicien / technicienne thermicienne", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.873"}, {"nom": "Maintenance d''installation de chauffage", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=I1308"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_872', 'huissier / huissière de justice', '<p>Délivrer une convocation à un particulier, faire exécuter une décision de justice, établir des constats... autant d''activités exercées par l''huissier de justice. Tous les actes de ce professionnel ont une valeur officielle.</p>', '[{"nom": "huissier / huissière de justice", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.872"}, {"nom": "Aide et médiation judiciaire", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K1901"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_875', 'pédicure-podologue', '<p>Le pédicure-podologue est spécialisé dans l''étude et le traitement des affections du pied et des ongles, à l''exclusion de toute intervention chirurgicale (sans effusion de sang).</p>', '[{"nom": "Pédicurie et podologie", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=J1409"}, {"nom": "pédicure-podologue", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.875"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_874', 'souffleur / souffleuse de verre', '<p>À partir de pâte de verre en fusion, le souffleur de verre donne naissance aux objets les plus somptueux (verres, vases...). Son secret : une main en or et une canne en acier, le tout dans un environnement extrêmement chaud.</p>', '[{"nom": "Réalisation d''objets artistiques et fonctionnels en verre", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=B1602"}, {"nom": "souffleur / souffleuse de verre", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.874"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_877', 'orthoptiste', '<p>Spécialiste du dépistage, de la rééducation et de la réadaptation oculaires, l''orthoptiste intervient notamment à la demande d''un ophtalmologiste pour mesurer le champ visuel ou déceler des strabismes.</p>', '[{"nom": "orthoptiste", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.877"}, {"nom": "Orthoptique", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=J1407"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_876', 'psychomotricien / psychomotricienne', '<p>Avoir le corps et l''esprit en harmonie pour dépasser les difficultés à la suite d''un accident ou d''une maladie, tel est l''objectif du psychomotricien. Il utilise le jeu et d''autres médiations pour rééduquer, notamment les troubles du geste, les problèmes d''orientation dans le temps ou dans l''espace, ou parvenir à mieux réguler ses émotions et s''adapter à son environnement.</p>', '[{"nom": "psychomotricien / psychomotricienne", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.876"}, {"nom": "Rééducation en psychomotricité", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=J1412"}]');
