INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_879', 'attaché / attachée de presse', '<p>Un article dans la presse écrite, un sujet de 1 min 30 au journal télévisé... voilà de quoi satisfaire tout attaché de presse ! Faire en sorte que les médias parlent de l''entreprise qu''il représente, tel est son pari au quotidien.</p>', '[{"nom": "Communication", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=E1103"}, {"nom": "attaché / attachée de presse", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.879"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_878', 'chargé / chargée HSE (hygiène sécurité environnement)', '<p>Le chargé ou la chargé HSE (hygiène sécurité environnement) participe à la définition de la politique de qualité et de sécurité des entreprises (personnels, matériels, conditions de travail, respect de l''environnement) et la met en oeuvre. Il ou elle assure la prévention des risques et accident qui pourraient survenir au sein d''une entreprise et remettre en cause la sécurité des salariés.</p>', '[{"nom": "chargé / chargée HSE (hygiène sécurité environnement)", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.878"}, {"nom": "Management et ingénierie Hygiène Sécurité Environnement -HSE- industriels", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1302"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_880', 'enseignant-chercheur / enseignante-chercheuse', '<p>Double mission pour l''enseignant-chercheur au sein d''une université ou d''une grande école : faire progresser la recherche dans sa discipline et transmettre les connaissances qui en sont issues à ses étudiants.</p>', '[{"nom": "Enseignement supérieur", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K2108"}, {"nom": "enseignant-chercheur / enseignante-chercheuse", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.880"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_882', 'ingénieur / ingénieure recherche et développement en agroalimentaire', '<p>Nouveaux produits ou emballages, nouvelles procédures de fabrication... l''ingénieur recherche et développement (R&amp;D) en agroalimentaire n''a qu''un mot d''ordre : innover ! Une condition indispensable pour les entreprises d''un secteur très concurrentiel.</p>', '[{"nom": "ingénieur / ingénieure recherche et développement en agroalimentaire", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.882"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_881', 'dépanneur / dépanneuse en électroménager', '<p>Le dépanneur en électroménager répare les produits dits " blancs " : lave-linge, lave-vaisselle, four, réfrigérateur... en intervenant aussi bien dans les lieux d''hébergement et de restauration, les hôpitaux... que dans les entreprises pour la maintenance de leurs équipements (distributeurs, matériels de cuisson...) ou les particuliers.</p>', '[{"nom": "Réparation de biens électrodomestiques et multimédia", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=I1402"}, {"nom": "dépanneur / dépanneuse en électroménager", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.881"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_884', 'ergonome', '<p>L''ergonome conçoit et améliore des lieux de vie, des objets ou des postes de travail afin de les adapter au maximum aux besoins des utilisateurs, en termes de confort, sécurité et efficacité.</p>', '[{"nom": "ergonome", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.884"}, {"nom": "Conseil en organisation et management d''entreprise", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=M1402"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_883', 'photographe', '<p>Faire des prises de vue, tel est l''objectif de tout photographe. Pourtant, les emplois se situent surtout dans le tirage et la vente, sous des statuts variés, et les lieux d''exercice sont très divers : laboratoire, studio, extérieur.</p>', '[{"nom": "Photographie", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=E1201"}, {"nom": "photographe", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.883"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_886', 'expert / experte automobile', '<p>Auto, moto, camion, bus, tracteur, moissonneuse, voire bateau... l''expert automobile est un généraliste du véhicule dont il connaît parfaitement la structure. Ses compétences techniques lui permettent d''en évaluer précisément l''état, d''en déceler les défauts, d''en apprécier la valeur et d''évaluer le coût de remise en état.</p>', '[{"nom": "Mécanique automobile et entretien de véhicules", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=I1604"}, {"nom": "expert / experte automobile", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.886"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_885', 'concepteur/trice de jeux vidéo', '<p>Qui seront les ennemis du héros ? Par quel artifice pourra-t-il s''échapper de sa prison ? Comment le soumettre à des épreuves de plus en plus difficiles ? Autant de questions que se pose le concepteur de jeux vidéo.</p>', '[{"nom": "Conception de contenus multimédias", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=E1104"}, {"nom": "concepteur / conceptrice de jeux vidéo", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.885"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_887', 'ingénieur / ingénieure télécoms et réseaux', '<p>L''ingénieur télécoms et réseaux participe à l''optimisation des systèmes de communication, de la recherche à la conception d''équipements et de services en passant par la gestion d''infrastructures réseaux.</p>', '[{"nom": "ingénieur / ingénieure télécoms et réseaux", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.887"}, {"nom": "Études et développement de réseaux de télécoms", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=M1804"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_889', 'matelot / matelote de la marine nationale', '<p>Fusiliers marins, cuisiniers, mécaniciens... Les matelots occupent diverses fonctions au sein des équipages de la marine nationale, à bord de bateaux ou sur les bases navales. Le matelot ou la matelote est avant tout un soldat ou une soldate, qui a signé un contrat avec la marine.</p>', '[{"nom": "matelot / matelote de la marine nationale", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.889"}, {"nom": "Equipage de la navigation maritime", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=N3102"}]');
