INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_80', 'formulateur / formulatrice', '<p>Un shampooing qui donne du volume aux cheveux, un fond de teint " 2 en 1 " avec effet hydratant renforcé... toutes ces créations sont l''oeuvre du formulateur, scientifique doté de créativité et de qualités sensorielles, qui maîtrise l''art des mélanges.</p>', '[{"nom": "formulateur / formulatrice", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.80"}, {"nom": "Intervention technique en formulation et analyse sensorielle", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1505"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_82', 'juriste d''entreprise', '<p>Protéger les intérêts de sa société sur les plans commercial, fiscal, social..., telle est la mission du juriste d''entreprise. Cela, qu''il soit généraliste ou spécialisé dans un domaine comme les contrats ou les contentieux.</p>', '[{"nom": "Défense et conseil juridique", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K1903"}, {"nom": "juriste d''entreprise", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.82"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_81', 'auditeur / auditrice interne', '<p>Comme un médecin généraliste, l''auditeur interne examine son entreprise, ses filiales et ses services sous tous les angles : commercial, comptable, fiscal, informatique... Son but : améliorer le fonctionnement et la productivité de sa société.</p>', '[{"nom": "auditeur / auditrice interne", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.81"}, {"nom": "Audit et contrôle comptables et financiers", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=M1202"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_84', 'testeur / testeuse en informatique', '<p>Le testeur est le spécialiste de la chasse aux bugs, ces erreurs qui empêchent le bon fonctionnement d''un logiciel. À lui de les signaler au service développement. Plus qu''une passion, c''est un métier à part entière.</p>', '[{"nom": "testeur / testeuse en informatique", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.84"}, {"nom": "Études et développement informatique", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=M1805"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_83', 'technicien / technicienne de maintenance en génie climatique', '<p>Le technicien de maintenance en génie climatique surveille le fonctionnement des équipements techniques et assure leur entretien et leur dépannage. Toujours disponible, il apporte une solution rapide et efficace aux clients victimes d''une panne.</p>', '[{"nom": "Installation et maintenance en froid, conditionnement d''air", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=I1306"}, {"nom": "technicien / technicienne de maintenance en génie climatique", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.83"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_75', 'ingénieur électricien / ingénieure électricienne', '<p>L''ingénieur électricien développe les réseaux d''électricité, conçoit des équipements électriques, améliore l''installation d''un hôpital, par exemple... Il travaille principalement chez les producteurs d''énergie, dans des entreprises industrielles ou du BTP (bâtiment et travaux publics).</p>', '[{"nom": "Management et ingénierie études, recherche et développement industriel", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1206"}, {"nom": "ingénieur électricien / ingénieure électricienne", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.75"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_74', 'ingénieur technico-commercial / ingénieure technico-commerciale', '<p>L''ingénieur technico-commercial se distingue par sa double compétence : la négociation commerciale et la connaissance parfaite des produits qu''il vend. Spécialiste du sur-mesure, il sait adapter son offre et ses services aux besoins de ses clients. </p>', '[{"nom": "ingénieur technico-commercial / ingénieure technico-commerciale", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.74"}, {"nom": "Relation technico-commerciale", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=D1407"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_77', 'animateur nature / animatrice nature', '<p>Sorties pour découvrir la flore et la faune locales, création de sentiers, organisation d''une exposition... l''animateur ou l''animatrice nature ne manque pas d''idées pour sensibiliser petits et grands à la préservation de l''environnement. De quoi former des écocitoyens.</p>', '[{"nom": "Animation d''activités culturelles ou ludiques", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=G1202"}, {"nom": "animateur nature / animatrice nature", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.77"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_76', 'chef / cheffe de cultures légumières', '<p>Les missions du chef de cultures légumières ? Répondre aux demandes des consommateurs (produire des légumes beaux, frais, savoureux et abordables), respecter l''environnement et s''adapter aux nouvelles technologies (informatique, électronique...).</p>', '[{"nom": "Horticulture et maraîchage", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=A1414"}, {"nom": "chef / cheffe de cultures légumières", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.76"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_79', 'ingénieur aromaticien / ingénieure aromaticienne', '<p>L''ingénieur aromaticien crée, reproduit ou adapte les saveurs de nos produits alimentaires, qu''ils soient salés ou sucrés. Formé aux techniques modernes de la chimie, c''est un familier des molécules d''origine chimique ou naturelle... et de leurs dosages. </p>', '[{"nom": "Intervention technique en formulation et analyse sensorielle", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1505"}, {"nom": "ingénieur aromaticien / ingénieure aromaticienne", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.79"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_78', 'façadier / façadière', '<p>Le métier de façadier est né avec l''arrivée des machines à projeter les enduits. À mi-chemin entre le gros oeuvre et la finition, ce professionnel a pour mission de protéger, imperméabiliser et isoler l''extérieur de nos habitations. </p>', '[{"nom": "façadier / façadière", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.78"}, {"nom": "Réalisation et restauration de façades", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=F1611"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_91', 'juge des contentieux de la protection', '<p>Travaux mal exécutés, loyers impayés, surendettement... Le juge des contentieux de la protection, anciennement appelé juge d''instance, juge les litiges les quotidien. Si cette profession attire de nombreux candidats, elle reste difficile d''accès.</p>', '[{"nom": "juge des contentieux de la protection", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.91"}, {"nom": "Magistrature", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K1904"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_90', 'aquaculteur / aquacultrice', '<p>L''aquaculteur est un agriculteur d''un genre un peu particulier puisque sa production est immergée (dans la mer, dans un étang ou dans un bassin). Élevant des poissons, des crustacés ou des fruits de mer, il veille à leur reproduction et s''occupe de leur commercialisation.</p>', '[{"nom": "Aquaculture", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=A1404"}, {"nom": "aquaculteur / aquacultrice", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.90"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_93', 'community manager', '<p>Garant de la présence et de la (bonne !) réputation d''une marque ou d''une entreprise sur les réseaux sociaux, le community manager anime une communauté d''internautes, publie des tweets, répond aux questions sur le Net, alimente la page Facebook...</p>', '[{"nom": "Animation de site multimédia", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=E1101"}, {"nom": "community manager", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.93"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_92', 'juriste en droit social', '<p>Plans sociaux, contrats de travail, réforme des retraites... Le droit social évolue constamment, au gré de nouveaux textes de loi. D''où la nécessité pour les entreprises et les administrations de faire appel à un juriste en droit social.</p>', '[{"nom": "juriste en droit social", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.92"}, {"nom": "Défense et conseil juridique", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K1903"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_95', 'ingénieur / ingénieure brevets', '<p>Une innovation peut valoir de l''or... si elle n''est pas copiée ! L''ingénieur brevets, spécialiste en propriété industrielle, veille sur les dernières trouvailles des entreprises et officialise leurs découvertes pour en garantir l''exclusivité. </p>', '[{"nom": "Management et ingénierie études, recherche et développement industriel", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1206"}, {"nom": "ingénieur / ingénieure brevets", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.95"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_94', 'maçon / maçonne', '<p>Premier à intervenir sur une construction, le maçon met en place les fondations des futurs immeubles, des maisons individuelles ou des bâtiments industriels. Puis il monte les éléments porteurs : murs, poutrelles et planchers.</p>', '[{"nom": "Maçonnerie", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=F1703"}, {"nom": "maçon / maçonne", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.94"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_86', 'metteur / metteuse en scène', '<p>S''approprier une oeuvre et la traduire sur scène, telle est l''ambition du metteur en scène. Grâce à ses directives avisées, textes, décors, costumes, éclairages, musique et jeu des acteurs s''assemblent en un tout artistique cohérent : un spectacle !</p>', '[{"nom": "Mise en scène de spectacles vivants", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=L1301"}, {"nom": "metteur / metteuse en scène", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.86"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_88', 'chocolatier/ère-confiseur/euse', '<p>Spécialiste de la fabrication de douceurs chocolatées ou à base de sucre, le chocolatier-confiseur, souvent artisan, est un artiste qui soigne la présentation de ses créations. À son compte ou dans l''industrie, il est reconnu pour son savoir-faire.</p>', '[{"nom": "chocolatier-confiseur / chocolatière-confiseuse", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.88"}, {"nom": "Pâtisserie, confiserie, chocolaterie et glacerie", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=D1104"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_89', 'médiateur / médiatrice familiale', '<p>Le médiateur familial est le spécialiste du conflit familial. Il intervient principalement dans des situations de séparation. Son rôle : trouver des solutions répondant aux besoins de chacun des membres de la famille.</p>', '[{"nom": "médiateur / médiatrice familiale", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.89"}, {"nom": "Accompagnement et médiation familiale", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K1101"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_60', 'économe de flux', '<p>Mission de l''économe de flux : faire baisser la facture d''énergie des entreprises et des collectivités (villes, administrations). À lui de repérer les surconsommations et de proposer des solutions pour économiser l''eau, le chauffage, l''électricité...</p>', '[{"nom": "économe de flux", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.60"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_62', 'bronzier / bronzière', '<p>Il y a 4000 ans, le bronze naissait du mélange du cuivre et de l''étain... Depuis, le bronzier travaille cet alliage pour créer ou restaurer des objets d''art ou décoratifs (sculptures, serrures, pièces d''ameublement...), grâce à des gestes traditionnels.</p>', '[{"nom": "Métallerie d''art", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=B1601"}, {"nom": "bronzier / bronzière", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.62"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_61', 'ingénieur/e en génie climatique', '<p>Obtenir une température et une qualité de l''air optimales dans des locaux professionnels ou des habitations, tout en réalisant des économies d''énergie et en respectant un cadre réglementaire strict et rigoureux : tel est le défi de cet ingénieur du BTP confronté aux enjeux du développement durable.</p>', '[{"nom": "ingénieur / ingénieure en génie climatique", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.61"}, {"nom": "Ingénierie et études du BTP", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=F1106"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_52', 'restaurateur/trice d''oeuvres d''art', '<p>Des tableaux aux couleurs assombries par le temps, une sculpture ternie par la pollution, une jarre brisée, un meuble endommagé... Le restaurateur d''oeuvres d''art est là pour donner une seconde jeunesse à tous ces objets.</p>', '[{"nom": "Décoration d''objets d''art et artisanaux", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=B1302"}, {"nom": "restaurateur / restauratrice d''oeuvres d''art", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.52"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_55', 'étanchéiste', '<p>Des infiltrations d''eau dans les murs, le sol ou le toit, et c''est tout un édifice qui peut s''écrouler. Pour éviter cela, l''étanchéiste pose des revêtements d''imperméabilisation. Un métier exigeant physiquement et très recherché.</p>', '[{"nom": "Travaux d''étanchéité et d''isolation", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=F1613"}, {"nom": "étanchéiste", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.55"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_54', 'matelot de la marine marchande', '<p>Charger, décharger, entretenir les machines, surveiller le pont, tenir la barre... autant de tâches dévolues au matelot de la marine marchande. Polyvalent, il exécute les ordres sur un navire transportant des passagers ou des marchandises. </p>', '[{"nom": "Equipage de la navigation maritime", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=N3102"}, {"nom": "matelot de la marine marchande", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.54"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_57', 'informaticien industriel / informaticienne industrielle', '<p>Dans l''industrie, l''informatique est omniprésente. Elle permet de piloter robots et automates. De la conception des produits à leur livraison, en passant par leur fabrication, les informaticiens industriels (techniciens et ingénieurs) se révèlent indispensables.</p>', '[{"nom": "informaticien industriel / informaticienne industrielle", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.57"}, {"nom": "Intervention technique en études et conception en automatisme", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1208"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_56', 'électricien installateur / électricienne installatrice', '<p>Soucieux de sécurité, l''électricien installateur réalise les installations électriques des maisons, des immeubles, des usines ou des centres commerciaux. Désormais, il s''occupe aussi de la pose de câbles informatiques, d''équipements de vidéosurveillance et de téléphonie.</p>', '[{"nom": "Électricité bâtiment", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=F1602"}, {"nom": "électricien installateur / électricienne installatrice", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.56"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_59', 'inspecteur / inspectrice des douanes, des finances publiques ou du travail', '<p>L''inspecteur employé par les Douanes, la DGFiP (Direction générale des finances publiques) ou l''Inspection du travail est un cadre de la fonction publique de catégorie A. Expert en fiscalité et en droit, il possède aussi des qualités humaines.</p>', '[{"nom": "Contrôle et inspection des Affaires Sociales", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K1502"}, {"nom": "inspecteur / inspectrice des douanes, des finances publiques ou du travail", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.59"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_58', 'contremaître', '<p>Le contremaître est un chef d''équipe. Au quotidien, il dirige des conducteurs de machines et travaille en collaboration avec les autres services, pour réaliser le programme de production prévu. Il s''assure que son équipe réponde aux commandes dans les délais.</p>', '[{"nom": "contremaître", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.58"}, {"nom": "Management et ingénierie gestion industrielle et logistique", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1401"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_71', 'garde (chasse, pêche, littoral, rivière, parcs nationaux)', '<p>Les gardes-chasse, gardes-pêche, gardes du littoral, gardes-rivière et gardes des parcs nationaux ont pour mission de protéger l''environnement, la faune et la flore par des actions de prévention, de répression et d''éducation.</p>', '[{"nom": "Protection du patrimoine naturel", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=A1204"}, {"nom": "garde (chasse, pêche, littoral, rivière, parcs nationaux)", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.71"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_70', 'apiculteur / apicultrice', '<p>« Éleveur » d''abeilles, l''apiculteur veille à leur bonne santé, en surveillant les ruches pour les protéger des prédateurs et des maladies. Il récolte la gelée royale, la cire et le miel fabriqués par les abeilles, et les commercialise en l''état ou après transformation.</p>', '[{"nom": "apiculteur / apicultrice", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.70"}, {"nom": "Élevage d''animaux sauvages ou de compagnie", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=A1408"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_73', 'géographe', '<p>Comprendre l''organisation de l''espace, tel est l''objectif du ou de la géographe. Ses recherches trouvent des applications dans de nombreux domaines : risques, aménagement, environnement, urbanisme, développement durable...</p>', '[{"nom": "géographe", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.73"}, {"nom": "Information géographique", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=M1808"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_72', 'greffier / greffière', '<p>Garder la trace des débats lors des audiences, authentifier les décisions de justice, être garant du respect des règles de procédure, telle est la mission du greffier. Un métier de rigueur et de précision.</p>', '[{"nom": "Collaboration juridique", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K1902"}, {"nom": "greffier / greffière", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.72"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_64', 'assistant / assistante de service social', '<p>L''ASS (assistant de service social) épaule des personnes en difficulté économique, sociale ou psychologique. Il joue aussi un rôle important pour la protection de l''enfance.</p>', '[{"nom": "assistant / assistante de service social", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.64"}, {"nom": "Action sociale", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K1201"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_63', 'encadreur / encadreuse', '<p>L''encadreur fabrique ou restaure un cadre pour une toile, un dessin ou une photographie. Son but est à la fois de les mettre en valeur et de les protéger de l''usure.</p>', '[{"nom": "Réalisation d''ouvrages décoratifs en bois", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H2208"}, {"nom": "encadreur / encadreuse", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.63"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_66', 'chargé / chargée de communication interne', '<p>Le chargé de communication interne est la courroie de transmission entre la direction et les salariés. Son rôle : informer pour rassembler le personnel autour de l''image et des valeurs de l''entreprise via un journal, l''intranet, un séminaire...</p>', '[{"nom": "Communication", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=E1103"}, {"nom": "chargé / chargée de communication interne", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.66"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_65', 'animateur socioculturel / animatrice socioculturelle', '<p>Atelier hip-hop, sortie culturelle, débats... l''animateur socioculturel organise des activités variées pour le public dont il s''occupe : enfants, adolescents, personnes âgées... Son objectif ? Favoriser le lien, la socialisation et la créativité.</p>', '[{"nom": "animateur socioculturel / animatrice socioculturelle", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.65"}, {"nom": "Intervention socioculturelle", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K1206"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_68', 'carrossier / carrossière', '<p>Changer un pare-chocs ou redresser de la tôle froissée, c''est l''affaire du carrossier réparateur. Au carrossier peintre de prendre ensuite le relais pour donner au véhicule son aspect définitif. Parfois, carrossier réparateur et carrossier peintre ne font qu''une seule et même personne.</p>', '[{"nom": "carrossier / carrossière", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.68"}, {"nom": "Réparation de carrosserie", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=I1606"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_67', 'secrétaire d''édition', '<p>La mission du secrétaire d''édition ? Transformer un manuscrit en livre, en suivant toutes les étapes de la réalisation d''un ouvrage, de la réception du texte jusqu''à son impression. Le tout sous l''autorité d''un responsable éditorial.</p>', '[{"nom": "Coordination d''édition", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=E1105"}, {"nom": "secrétaire d''édition", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.67"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_69', 'accessoiriste', '<p>Dénicher, réparer, fabriquer des objets, telles sont les missions de l''accessoiriste. Objectif : donner un côté réaliste à un projet artistique tout en restant fidèle aux intentions du metteur en scène.</p>', '[{"nom": "accessoiriste", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.69"}, {"nom": "Décor et accessoires spectacle", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=L1503"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_97', 'créateur / créatrice textile', '<p>Le métier de créateur textile est à la fois créatif et technique. C''est lui qui imagine et crée les tissus qui, demain, serviront à fabriquer vêtements, nappes, rideaux, etc.</p>', '[{"nom": "créateur / créatrice textile", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.97"}, {"nom": "Stylisme", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=B1805"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_96', 'conseiller / conseillère microcrédit', '<p>À l''écoute, la conseillère ou le conseiller microcrédit reçoit des personnes souhaitant obtenir une petite somme d''argent pour réaliser un projet. Évaluation, montage et défense du dossier, suivi des remboursements font partie de ses tâches.</p>', '[{"nom": "conseiller / conseillère microcrédit", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.96"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_99', 'vitrailliste', '<p>Chartres, La Sainte-Chapelle de Paris... Du roman au gothique, l''art du vitrail a connu son âge d''or. Aujourd''hui, le vitrailliste partage son temps entre restauration et création. Il est à la fois artiste et technicien du verre.</p>', '[{"nom": "vitrailliste", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.99"}, {"nom": "Réalisation d''objets artistiques et fonctionnels en verre", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=B1602"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_8', 'chef / cheffe de publicité', '<p>Commercial en régie publicitaire, conseiller en communication ou en agence de publicité, le métier n''est pas le même. En régie, le chef de publicité vend des espaces publicitaires pour les supports qu''il représente. En agence, il gère un budget de communication.</p>', '[{"nom": "chef / cheffe de publicité", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.8"}, {"nom": "Développement et promotion publicitaire", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=E1401"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_7', 'chanteur / chanteuse', '<p>Opéra, jazz, rock, variétés... autant de voies pour une seule voix ! Mais ne devient pas chanteur qui veut : les passionnés devront fournir des efforts et s''entraîner sans relâche pour espérer gagner les faveurs du public.</p>', '[{"nom": "chanteur / chanteuse", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.7"}, {"nom": "Musique et chant", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=L1202"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_9', 'charpentier / charpentière bois', '<p>Le charpentier conçoit, fabrique et pose des charpentes et ossatures en bois qui servent de structures à des maisons et autres constructions. C''est un métier à forte tradition qui se modernise et recrute des jeunes qualifiés.</p>', '[{"nom": "charpentier / charpentière bois", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.9"}, {"nom": "Montage de structures et de charpentes bois", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=F1501"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_4', 'sommelier / sommelière', '<p>Expert dans l''art de servir et de déguster le vin, le sommelier a le palais fin, l''odorat subtil et le don de faire partager ses coups de coeur aux clients du restaurant dans lequel il travaille. Il navigue entre la cave qu''il gère et le restaurant.</p>', '[{"nom": "Sommellerie", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=G1804"}, {"nom": "sommelier / sommelière", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.4"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_3', 'réceptionniste', '<p>Ambassadeur de l''hôtel, le réceptionniste accueille les clients et gère les réservations. Il informe et répond aux questions, prépare les factures... Un poste tremplin à responsabilités où l''organisation et le sang-froid sont indispensables.</p>', '[{"nom": "réceptionniste", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.3"}, {"nom": "Réception en hôtellerie", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=G1703"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_6', 'musicien / musicienne', '<p>Créer des mélodies, les interpréter, transmettre son savoir... Le métier de musicien recouvre une multitude de spécificités : à chacun de choisir sa propre musique, de jouer sa propre histoire !</p>', '[{"nom": "musicien / musicienne", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.6"}, {"nom": "Musique et chant", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=L1202"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_5', 'designer graphique', '<p>Affiches, logos, emballages, sites web... l''empreinte du designer graphique est partout : dans la presse, l''édition en passant par la publicité, la communication et le design... Sa mission : concevoir un visuel, alliant sens artistique aux enjeux commerciaux et marketing, qui saura séduire le public.</p>', '[{"nom": "designer graphique", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.5"}, {"nom": "Réalisation de contenus multimédias", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=E1205"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_2', 'directeur / directrice d''hôtel', '<p>Animateur d''équipe, gestionnaire et commercial, le directeur d''hôtel a tout du chef d''entreprise. Ses responsabilités varient fortement selon le type d''établissement qu''il dirige, mais exigent une présence de tous les instants.</p>', '[{"nom": "directeur / directrice d''hôtel", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.2"}, {"nom": "Management d''hôtel-restaurant", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=G1402"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_1', 'contrôleur aérien / contrôleuse aérienne', '<p>Garant de la sécurité et de la fluidité du trafic aérien, le contrôleur aérien guide les pilotes du décollage jusqu''à l''atterrissage de leur avion. Un métier scientifique à haute responsabilité, sans droit à l''erreur !</p>', '[{"nom": "contrôleur aérien / contrôleuse aérienne", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.1"}, {"nom": "Contrôle de la navigation aérienne", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=N2202"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_194', 'régisseur général / régisseuse générale cinéma', '<p>Lors du tournage d''un film, le régisseur général assure la logistique. Il accueille les comédiens, trouve des places de parking, achemine le matériel technique... À lui de gérer le quotidien des plateaux pour que tout se passe au mieux.</p>', '[{"nom": "Régie générale", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=L1509"}, {"nom": "régisseur général / régisseuse générale cinéma", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.194"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_193', 'chargé / chargée de production', '<p>Le chargé de production collabore à la création d''un film, d''une émission de télévision, d''un spectacle. Responsable du budget, il supervise la production et assure la gestion matérielle et humaine du projet.</p>', '[{"nom": "Production et administration spectacle, cinéma et audiovisuel", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=L1302"}, {"nom": "chargé / chargée de production", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.193"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_196', 'responsable de rémunération', '<p>Si les ressources humaines se sont diversifiées, l''administration de la paie n''en reste pas moins une fonction clé dans l''entreprise. De bac + 3 à bac + 5, les jeunes diplômés peuvent s''y faire une place en tant que responsable de rémunération.</p>', '[{"nom": "responsable de rémunération", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.196"}, {"nom": "Management des ressources humaines", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=M1503"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_195', 'chef / cheffe comptable', '<p>Le chef comptable ne se contente pas de collecter les chiffres de sa société. Il les analyse et en tire des bilans et des synthèses. Il est ainsi l''interlocuteur du chef d''entreprise ou du directeur financier, qu''il aide à prendre des décisions.</p>', '[{"nom": "chef / cheffe comptable", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.195"}, {"nom": "Management de groupe ou de service comptable", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=M1206"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_198', 'technicien électronicien / technicienne électronicienne', '<p>Le technicien électronicien répare, installe et fabrique des produits intégrant des composantes électroniques : appareils audio-vidéo, instruments de mesure, systèmes de navigation, etc. Assistant de l''ingénieur, il maîtrise de multiples compétences.</p>', '[{"nom": "Installation et maintenance électronique", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=I1305"}, {"nom": "technicien électronicien / technicienne électronicienne", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.198"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_197', 'technicien / technicienne paysagiste', '<p>Le technicien ou la technicienne paysagiste travaille rarement en solo : c''est toute une équipe qu''il lui faut gérer pour réaliser jardins et espaces verts, en se conformant aux plans du paysagiste concepteur.</p>', '[{"nom": "technicien / technicienne paysagiste", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.197"}, {"nom": "Aménagement et entretien des espaces verts", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=A1203"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_199', 'vendeur-conseil / vendeuse-conseil en matériel agricole', '<p>Le vendeur-conseil en matériel agricole assure la vente des tracteurs et autres équipements agricoles. Un métier qui exige patience et compétences techniques. Mais, quand on a le bon profil, l''emploi est au rendez-vous.</p>', '[{"nom": "vendeur-conseil / vendeuse-conseil en matériel agricole", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.199"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_192', 'géomaticien/ne', '<p>À la croisée de la géographie et de l''informatique, le géomaticien ou la géomaticienne exploite les données pour modéliser le territoire. Il ou elle intervient dans tous les secteurs qui ont besoin d''analyse spatiale : urbanisme, environnement, transport, énergie, assurance...</p>', '[{"nom": "géomaticien / géomaticienne", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.192"}, {"nom": "Information géographique", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=M1808"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_191', 'officier / officière de police', '<p>Mener des enquêtes, c''est le rôle de l''officière ou de l''officier de police. Dans les différents services de la Police nationale, ce professionnel ou cette professionnelle de terrain assure également des missions de surveillance et de renseignement.</p>', '[{"nom": "officier / officière de police", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.191"}, {"nom": "Management de la sécurité publique", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K1704"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_172', 'documentaliste', '<p>Dans une entreprise, une bibliothèque, un musée, un journal..., ce spécialiste des technologies de l''information et de la communication collecte, classe et rend accessible l''information quel qu''en soit le support.</p>', '[{"nom": "documentaliste", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.172"}, {"nom": "Gestion de l''information et de la documentation", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K1601"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_171', 'vétérinaire', '<p>Les vétos ? En ville, leurs patients sont des chiens, des chats, des rongeurs... À la campagne, ce sont les animaux de la ferme. Dans les deux cas, les vétérinaires conseillent les propriétaires des animaux de compagnie ou les éleveurs, mais d''autres débouchés sont possibles.</p>', '[{"nom": "vétérinaire", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.171"}, {"nom": "Santé animale", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=A1504"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_174', 'viticulteur / viticultrice', '<p>Pour cultiver la vigne, récolter le raisin, le vinifier puis vendre le vin, le viticulteur doit travailler dur et apprendre en permanence. Dans un pays réputé pour ses grands crus, les amateurs n''ont pas leur place.</p>', '[{"nom": "viticulteur / viticultrice", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.174"}, {"nom": "Arboriculture et viticulture", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=A1405"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_176', 'sérigraphe', '<p>De l''affiche à la carte à puce, la sérigraphie est partout ! Cette technique d''impression permet de reproduire un motif sur différents types de supports et matériaux. Une technique artisanale dont l''industrie s''est peu à peu emparée.</p>', '[{"nom": "Conduite de machines d''impression", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=E1301"}, {"nom": "sérigraphe", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.176"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_175', 'technicien / technicienne qualité', '<p>Quel que soit le produit fabriqué (carton d''emballage, papier essuie-tout, etc.) le technicien qualité est là pour en contrôler la conformité par rapport au cahier des charges initial et garantir sa traçabilité en cas de défaut.</p>', '[{"nom": "technicien / technicienne qualité", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.175"}, {"nom": "Intervention technique en laboratoire d''analyse industrielle", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1503"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_178', 'assistant réalisateur / assistante réalisatrice', '<p>Organiser les repérages, mettre au point le plan de travail, fixer l''heure du maquillage... l''assistant réalisateur est le collaborateur direct du réalisateur. Il assure le lien entre ce dernier, les acteurs et l''équipe technique.</p>', '[{"nom": "Réalisation cinématographique et audiovisuelle", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=L1304"}, {"nom": "assistant réalisateur / assistante réalisatrice", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.178"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_177', 'tapissier / tapissière d''ameublement', '<p>Le tapissier d''ameublement restaure ou conçoit fauteuils et literie. Il décore murs et fenêtres à l''aide de tissus, rideaux, coussins, têtes de lit… Garant de l''harmonie et du style (classique, baroque, contemporain, etc.), il travaille dans les règles de l''art.</p>', '[{"nom": "tapissier / tapissière d''ameublement", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.177"}, {"nom": "Tapisserie - décoration en ameublement", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=B1806"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_170', 'ingénieur / ingénieure chimiste', '<p>Pétrochimie, agroalimentaire, pharmaceutique, colorants, transformation des plastiques... Dans de nombreuses industries, l''ingénieur chimiste participe à la recherche et développement, à la production... L''environnement fait aussi partie de ses missions.</p>', '[{"nom": "Management et ingénierie de production", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H2502"}, {"nom": "ingénieur / ingénieure chimiste", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.170"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_179', 'démographe', '<p>Spécialiste des enquêtes et de la statistique, le démographe analyse les impacts économiques et sociaux des phénomènes démographiques. Fécondité, mortalité, migration, répartitions géographiques... sont au coeur de ses études.</p>', '[{"nom": "démographe", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.179"}, {"nom": "Recherche en sciences de l''homme et de la société", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K2401"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_182', 'ingénieur / ingénieure du BTP', '<p>Très qualifié en sciences et techniques, l''ingénieur du BTP (bâtiment et travaux publics) est un cadre qui travaille en équipe, prend des décisions et assume des responsabilités au sein d''une entreprise. Il développe les infrastructures d''une région ou d''un pays, en concevant ponts, barrages, bâtiments, routes...</p>', '[{"nom": "ingénieur / ingénieure du BTP", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.182"}, {"nom": "Ingénierie et études du BTP", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=F1106"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_185', 'agent / agente de sûreté ferroviaire', '<p>En uniforme et assermenté, l''agent de la sûreté ferroviaire protège les voyageurs, surveille les gares, les installations et les équipements de la SNCF, les trains de marchandises, etc. Il est formé pour prévenir ou intervenir si besoin.</p>', '[{"nom": "agent / agente de sûreté ferroviaire", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.185"}, {"nom": "Sécurité et surveillance privées", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K2503"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_184', 'ingénieur pétrolier / ingénieure pétrolière', '<p>Après repérage des gisements potentiels d''hydrocarbures par les géologues et les géophysiciens, l''ingénieur pétrolier procède à leur exploitation, calcule leur rendement, installe les puits, produit et raffine le pétrole.</p>', '[{"nom": "ingénieur pétrolier / ingénieure pétrolière", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.184"}, {"nom": "Management et ingénierie Hygiène Sécurité Environnement -HSE- industriels", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1302"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_187', 'machiniste-constructeur ou plateau / machiniste-constructrice ou plateau', '<p>Il n''a pas son pareil pour faire apparaître et disparaître un décor. Peu reconnu, le machiniste-constructeur est pourtant indispensable. Un métier tremplin pour accéder à d''autres fonctions.</p>', '[{"nom": "machiniste-constructeur ou plateau / machiniste-constructrice ou plateau", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.187"}, {"nom": "Machinerie spectacle", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=L1506"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_188', 'brodeur / brodeuse', '<p>Le brodeur ajoute des éléments de décoration à la surface d''un tissu afin de l''enrichir et de le mettre en valeur. Perpétuant des savoir-faire traditionnels, il est artisan d''art ou travaille pour l''industrie textile sur des machines plus complexes.</p>', '[{"nom": "brodeur / brodeuse", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.188"}, {"nom": "Réalisation d''ouvrages d''art en fils", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=B1804"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_181', 'magasinier / magasinière cariste', '<p>Boulons, conserves, verres... quelle que soit la nature des marchandises, le magasinier cariste planifie leur livraison, les réceptionne, les transporte, les stocke et les expédie. Un métier nécessaire, qui a su évoluer avec les technologies.</p>', '[{"nom": "Magasinage et préparation de commandes", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=N1103"}, {"nom": "magasinier / magasinière cariste", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.181"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_180', 'sociologue', '<p>Qu''est-ce qu''une famille ? Quand cette notion est-elle apparue ? Voici le type de questions auxquelles tente de répondre un sociologue. Son objectif : comprendre comment fonctionne une société.</p>', '[{"nom": "sociologue", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.180"}, {"nom": "Recherche en sciences de l''homme et de la société", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K2401"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_150', 'marchandiseur / marchandiseuse', '<p>Les bonbons près des caisses de supermarchés, c''est lui. Les vins du mois en tête de rayon, c''est encore lui. Les présentoirs pour les promos, les vitrines des boutiques de mode, c''est toujours lui. Le marchandiseur met en scène les produits pour qu''ils soient remarqués... et achetés !</p>', '[{"nom": "marchandiseur / marchandiseuse", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.150"}, {"nom": "Marchandisage", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=D1506"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_152', 'pâtissier / pâtissière', '<p>Spécialiste des desserts, le pâtissier maîtrise les techniques classiques (pâtes, crèmes, nappages...) et apporte sa touche personnelle. Son savoir-faire est très recherché dans l''artisanat, la restauration ou la pâtisserie industrielle.</p>', '[{"nom": "Boulangerie - viennoiserie", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=D1102"}, {"nom": "pâtissier / pâtissière", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.152"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_151', 'caissier / caissière', '<p>En grande surface, au guichet d''un stade, d''un théâtre ou d''un musée, le caissier reçoit les clients au moment d''encaisser le paiement de leurs achats. Il est aussi un contact primordial pour l''image de marque de l''enseigne. </p>', '[{"nom": "caissier / caissière", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.151"}, {"nom": "Personnel de caisse", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=D1505"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_153', 'charcutier-traiteur / charcutière-traiteuse', '<p>Des terrines aux plats cuisinés en passant par les saucisses ou le jambon, le charcutier-traiteur prépare et vend une multitude de produits. À mi-chemin entre la boucherie et la cuisine, ce métier complet offre de très bons débouchés.</p>', '[{"nom": "Charcuterie - traiteur", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=D1103"}, {"nom": "charcutier-traiteur / charcutière-traiteuse", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.153"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_156', 'ingénieur forestier / ingénieure forestière', '<p>Avec près de 15 millions d''hectares, la forêt française a besoin de professionnels. L''ingénieur forestier en fait partie. Il gère les projets d''aménagement et d''exploitation de cet immense territoire, tout en respectant l''environnement.</p>', '[{"nom": "Ingénierie en agriculture et environnement naturel", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=A1303"}, {"nom": "ingénieur forestier / ingénieure forestière", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.156"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_158', 'paysagiste', '<p>Voilà longtemps que le ou la paysagiste ne se contente plus de fleurir les carrefours. Véritable architecte des espaces verts, il ou elle fait appel à sa créativité pour modeler les villes et les campagnes. Son objectif : améliorer notre cadre de vie.</p>', '[{"nom": "Ingénierie en agriculture et environnement naturel", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=A1303"}, {"nom": "paysagiste", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.158"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_157', 'horticulteur / horticultrice', '<p>L''horticulteur cultive les jardins potagers, floraux, d''ornement et d''agrément. Cette appellation générale regroupe d''autres spécialistes : le floriculteur, le pépiniériste, le maraîcher et l''arboriculteur.</p>', '[{"nom": "horticulteur / horticultrice", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.157"}, {"nom": "Horticulture et maraîchage", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=A1414"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_159', 'oenologue', '<p>Scientifique au palais développé, l''oenologue est un chef d''orchestre dans le secteur vinicole. Salarié ou indépendant, il analyse et supervise tous les stades de la production du vin, du cep de vigne à la table. Son credo : la qualité.</p>', '[{"nom": "oenologue", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.159"}, {"nom": "Fermentation de boissons alcoolisées", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=A1413"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_161', 'architecte', '<p>Construction d''un immeuble ou d''un hôpital, réhabilitation de logements sociaux, reconversion d''une usine en bureaux... Autant de projets qui ne peuvent pas voir le jour sans l''intervention d''un architecte. Ce maître d''oeuvre exerce le plus souvent en libéral.</p>', '[{"nom": "Architecture du BTP et du paysage", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=F1101"}, {"nom": "architecte", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.161"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_160', 'opérateur / opératrice sur machine à commande numérique', '<p>Partout où les machines à commande numérique sont présentes, l''opérateur est là pour les programmer, les alimenter, les décharger et veiller à ce que tout se passe au mieux, tout au long de la chaîne de fabrication.</p>', '[{"nom": "Conduite d''équipement d''usinage", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H2903"}, {"nom": "opérateur / opératrice sur machine à commande numérique", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.160"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_163', 'puériculteur/trice', '<p>Le puériculteur ou la puéricultrice est un infirmier ou une infirmière spécialisée dans les soins médicaux apportés aux bébés et aux enfants. Il ou elle joue également un rôle de prévention, d''éducation et de conseil auprès des parents.</p>', '[{"nom": "puériculteur / puéricultrice", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.163"}, {"nom": "Soins infirmiers spécialisés en puériculture", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=J1507"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_162', 'coffreur-boiseur / coffreuse-boiseuse', '<p>Constructeur de moules pour le béton armé, le coffreur-boiseur est un ouvrier du bâtiment très demandé. C''est un professionnel incontournable sur les grands chantiers de construction et de travaux publics.</p>', '[{"nom": "coffreur-boiseur / coffreuse-boiseuse", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.162"}, {"nom": "Construction en béton", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=F1701"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_165', 'technicien / technicienne en optique de précision', '<p>Espace, médecine, lunetterie, photographie, contrôle de la pollution... Autant de secteurs où il peut trouver sa place. Le technicien en optique de précision participe, avec le chercheur ou l''ingénieur, à la conception et à la réalisation d''instruments d''optique de haute technologie.</p>', '[{"nom": "technicien / technicienne en optique de précision", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.165"}, {"nom": "Intervention technique en études, recherche et développement", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1210"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_164', 'météorologiste', '<p>Spécialiste des phénomènes atmosphériques, le ou la météorologiste étudie et analyse les causes et les effets des changements climatiques. Il ou elle établit des prévisions et anticipe les risques de catastrophe naturelle (avalanche, séisme, inondation...).</p>', '[{"nom": "Information météorologique", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=M1809"}, {"nom": "météorologiste", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.164"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_166', 'archéologue', '<p>L''archéologue est un spécialiste des " choses " anciennes. C''est un chercheur qui étudie les traces laissées par l''homme depuis la Préhistoire. Ses découvertes permettent de mieux connaître et comprendre les modes de vie des sociétés du passé.</p>', '[{"nom": "archéologue", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.166"}, {"nom": "Recherche en sciences de l''homme et de la société", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K2401"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_169', 'technicien / technicienne d''exploitation du réseau gaz', '<p>Assurer les branchements, la surveillance et l''entretien des canalisations qui acheminent le gaz dans toute la France, c''est le rôle du technicien d''exploitation du réseau gaz. Il prépare et contrôle des activités de maintenance sur le terrain.</p>', '[{"nom": "technicien / technicienne d''exploitation du réseau gaz", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.169"}, {"nom": "Pilotage d''installation énergétique et pétrochimique", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H2701"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_168', 'collaborateur de notaire / collaboratrice de notaire', '<p>Sous la responsabilité du notaire, le collaborateur prépare et rédige les actes ponctuant la vie des familles et des entreprises : mariage, achat ou vente de propriété, succession, donation...</p>', '[{"nom": "collaborateur de notaire / collaboratrice de notaire", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.168"}, {"nom": "Collaboration juridique", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K1902"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_130', 'urbaniste concepteur/trice', null, '[]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_132', 'Médiateur social / médiatrice sociale', e'Les médiateurs sociaux exercent en zones sensibles pour prévenir ou tenter de remédier à des problèmes d\'incivilité. Ils mènent des actions de prévention sur le terrain, et leurs missions dépendent de l\'organisme qui les emploie.<h3>Après la 3<sup>e</sup></h3>
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_136', 'conservateur territorial des bibliothèques / conservatrice territoriale des bibliothèques', '<p>Le conservateur territorial de bibliothèques occupe des fonctions de direction au sein d''une bibliothèque, d''une médiathèque ou d''une direction culturelle. Il développe la diffusion des ressources de son établissement et manage son équipe dans cet objectif.</p>', '[{"nom": "conservateur territorial des bibliothèques / conservatrice territoriale des bibliothèques", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.136"}, {"nom": "Gestion de l''information et de la documentation", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K1601"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_135', 'vendeur/euse d''articles de sport', null, '[]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_138', 'ingénieur/e d''affaires en aéronautique', null, '[]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_143', 'technicien / technicienne logistique', '<p>Le technicien logistique contrôle, en qualité comme en quantité, toutes les entrées et toutes les sorties de marchandises dans l''entrepôt. Il veille à respecter un difficile équilibre : minimiser les stocks sans pour autant risquer la rupture.</p>', '[{"nom": "Intervention technique d''exploitation logistique", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=N1303"}, {"nom": "technicien / technicienne logistique", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.143"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_145', 'barman / barmaid', '<p>Dynamique et adroit, le barman incarne l''âme et l''ambiance d''un bar. Il prépare et sert les boissons, des plus classiques aux plus sophistiquées. Il doit se montrer discret et diplomate, tout en restant à l''écoute de ses clients.</p>', '[{"nom": "Café, bar brasserie", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=G1801"}, {"nom": "barman / barmaid", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.145"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_144', 'directeur / directrice de restaurant', '<p>De la cuisine à l''accueil des clients, en passant par le service et la gestion, le directeur de restaurant est partout. Sa mission : tout mettre en oeuvre pour faire de son établissement un lieu apprécié de la clientèle.</p>', '[{"nom": "Management d''hôtel-restaurant", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=G1402"}, {"nom": "directeur / directrice de restaurant", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.144"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_146', 'comédien / comédienne', '<p>Incarner un rôle sur scène ou à l''écran, apparaître dans un téléfilm ou un spot publicitaire, doubler un personnage... Quelques-unes des multiples facettes du métier de comédien.</p>', '[{"nom": "comédien / comédienne", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.146"}, {"nom": "Art dramatique", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=L1203"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_149', 'expert-comptable / experte-comptable', '<p>L''activité principale de l''expert-comptable est le conseil aux entreprises. Au-delà de la comptabilité, il apporte son expertise en matière financière, fiscale ou juridique. Partenaire clé du dirigeant, il l''accompagne dans ses prises de décisions.</p>', '[{"nom": "Audit et contrôle comptables et financiers", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=M1202"}, {"nom": "expert-comptable / experte-comptable", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.149"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_148', 'serrurier-métallier / serrurière-métallière', '<p>Il dompte les métaux pour les transformer en rampe d''escalier, en balcon, en clés, en pièces de charpente, fenêtre... Lui, c''est le serrurier-métallier. A ne pas confondre avec le serrurier-dépanneur.</p>', '[{"nom": "Réalisation de structures métalliques", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H2911"}, {"nom": "serrurier-métallier / serrurière-métallière", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.148"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_110', 'maquilleur / maquilleuse artistique', '<p>Faux nez, masques... le maquilleur artistique transforme les acteurs selon les indications du metteur en scène ou du réalisateur. Ce professionnel créatif donne vie à des personnages imaginaires pour le cinéma, le théâtre ou les spectacles vivants.</p>', '[{"nom": "maquilleur / maquilleuse artistique", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.110"}, {"nom": "Coiffure et maquillage spectacle", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=L1501"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_112', 'critique d''art', '<p>Rendre compte en quelques lignes d''une actualité artistique, rencontrer des artistes, visiter des expositions, participer à des conférences... telles sont quelques-unes des nombreuses activités du critique d''art. </p>', '[{"nom": "Journalisme et information média", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=E1106"}, {"nom": "critique d''art", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.112"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_111', 'médecin de secours en montagne', '<p>Allô, le 112 ? Le médecin de secours en montagne embarque à bord de l''hélicoptère aux côtés des sauveteurs. Déposés en montagne par le pilote, les sauveteurs sécurisent le lieu, tandis que le médecin se charge des victimes.</p>', '[{"nom": "Médecine généraliste et spécialisée", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=J1102"}, {"nom": "médecin de secours en montagne", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.111"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_114', 'localisateur/trice', '<p>Trouver le mot juste, c''est la préoccupation constante du localisateur ou de la localisatrice en charge de l''adaptation d''un jeu vidéo ou d''un logiciel étranger pour les utilisateurs de son pays. Il ou elle apporte une couleur locale au produit multimédia.</p>', '[{"nom": "localisateur / localisatrice", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.114"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_113', 'responsable qualité en agroalimentaire', '<p>Personnage-clé de l''industrie agroalimentaire, le responsable qualité en agroalimentaire est chargé de contrôler toute la chaîne de fabrication d''un produit et de garantir la sécurité des aliments qui sortent de l''usine.</p>', '[{"nom": "responsable qualité en agroalimentaire", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.113"}, {"nom": "Management et ingénierie qualité industrielle", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1502"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_116', 'conducteur / conductrice de travaux en entreprises de travaux agricoles', '<p>Le conducteur de travaux en entreprise de travaux agricoles, forestiers ou ruraux fait le lien entre son entreprise et le client. Il va sur le terrain pour évaluer les besoins, puis établit le devis. Il veille au bon déroulement du chantier et au respect des délais.</p>', '[{"nom": "Polyculture, élevage", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=A1416"}, {"nom": "conducteur / conductrice de travaux en entreprises de travaux agricoles", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.116"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_115', 'moniteur/trice d''escalade', null, '[]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_118', 'formateur/trice technique en agroéquipement', '<p>Le formateur technique est chargé de former des professionnels à l''utilisation ou à la réparation d''un nouvel équipement ou outil. Très recherché, il travaille généralement chez un constructeur, un importateur ou un distributeur.</p>', '[{"nom": "formateur / formatrice technique en agroéquipement", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.118"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_117', 'technicien / technicienne prototypiste en agroéquipement', '<p>Le technicien prototypiste en agroéquipement fait le lien entre le bureau d''études et la fabrication industrielle du produit. Il construit tout ou une partie d''un nouvel engin qu''il teste et modifie avant la production. Il allie bon niveau technique et créativité.</p>', '[{"nom": "technicien / technicienne prototypiste en agroéquipement", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.117"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_121', 'Traiteur organisateur de réception/ traiteuse organisatrice de réception', e'Un mariage, une soirée de remise de trophées, une réception commerciale, un festival : le traiteur organisateur ou la traiteuse organisatrice de réceptions est là pour superviser la préparation et le service du banquet. Ce ou cette spécialiste de l\'événementiel sait concocter des menus alléchants en tenant compte du budget de sa clientèle.<h3>Après la 3<sup>e</sup></h3>
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_120', 'ouvrier / ouvrière paysagiste', '<p>L''ouvrière ou l''ouvrier paysagiste aménage et entretient les jardins des particuliers ou les espaces verts publics. Selon le cas, ces professionnels sont des agents du service public ou des employés d''une entreprise du paysage.</p>', '[{"nom": "ouvrier / ouvrière paysagiste", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.120"}, {"nom": "Aménagement et entretien des espaces verts", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=A1203"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_122', 'administrateur territorial / administratrice territoriale', '<p>L''administrateur territorial correspond à un statut dans la fonction publique qui permet d''accéder à des responsabilités de direction dans une collectivité territoriale (ville de plus de 40 000 habitants, département, région, structure intercommunale...).</p>', '[{"nom": "administrateur territorial / administratrice territoriale", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.122"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_125', 'ingénieur/e analogicien/ne', '<p>Lorsque l''électronique numérique ne donne pas un résultat satisfaisant, l''électronique analogique la complète dans certains domaines, notamment pour les puristes des images et des sons. L''ingénieur analogicien, fortement spécialisé, est recherché. Il travaille en équipe pour concevoir des circuits intégrés analogiques.</p>', '[{"nom": "Management et ingénierie études, recherche et développement industriel", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1206"}, {"nom": "ingénieur analogicien / ingénieure analogicienne", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.125"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_124', 'ingénieur/e études et développement en logiciels de simulation', '<p>L''ingénieur études et développement en logiciels de simulation conçoit et développe les logiciels embarqués dans les cockpits des avions civils et militaires, à partir de spécifications établies avec les avionneurs.</p>', '[{"nom": "ingénieur / ingénieure études et développement en logiciels de simulation", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.124"}, {"nom": "Études et développement informatique", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=M1805"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_126', 'biostatisticien/ne', '<p> Aidant les chercheurs à planifier leurs expériences et à interpréter les résultats, le biostatisticien utilise ses compétences statistiques et informatiques dans les domaines de la santé, de la biologie, de l''agroalimentaire... Un métier de chiffres et d''équations.</p>', '[{"nom": "Recherche en sciences de l''univers, de la matière et du vivant", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K2402"}, {"nom": "biostatisticien / biostatisticienne", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.126"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_128', 'technicien/ne contrôle qualité aéronautique', null, '[]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_101', 'conseiller / conseillère en fusions-acquisitions', '<p>Rachat de sociétés, vente de filiales, fusions : le conseiller en fusions-acquisitions guide les entreprises dans la réalisation d''opérations financières complexes. Un métier où l''intuition et le sens des affaires sont essentiels.</p>', '[{"nom": "Analyse et ingénierie financière", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=M1201"}, {"nom": "conseiller / conseillère en fusions-acquisitions", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.101"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_100', 'développeur rural / développeuse rurale humanitaire', '<p>Le développeur rural humanitaire conseille les populations vulnérables dans les pays en développement. Son objectif : les conduire vers l''autosuffisance alimentaire dans une perspective de développement durable.</p>', '[{"nom": "développeur rural / développeuse rurale humanitaire", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.100"}, {"nom": "Développement local", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K1802"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_103', 'ingénieur / ingénieure en production et expérimentations végétales', '<p>Céréales OGM (organismes génétiquement modifiés), croisements d''optimisation pour créer des fleurs exceptionnelles... l''ingénieur en production et expérimentations végétales innove, dans le respect des exigences éthiques et sociales et des normes en vigueur.</p>', '[{"nom": "Management et ingénierie de production", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H2502"}, {"nom": "ingénieur / ingénieure en production et expérimentations végétales", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.103"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_105', 'géologue modélisateur/trice', '<p>À l''aise sur le terrain comme devant son ordinateur à manipuler des logiciels 3D perfectionnés, le géologue modélisateur ou la géologue modélisatrice permet, par exemple, d''éviter une pollution du sol en suivant le cheminement d''un produit, ou de prévisualiser un forage.</p>', '[{"nom": "Études géologiques", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=F1105"}, {"nom": "géologue modélisateur / modélisatrice", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.105"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_104', 'ingénieur/e technico-commerciale en électronique', '<p>Doué pour la négociation, l''ingénieur technico-commercial en électronique connaît parfaitement les produits qu''il vend (circuits intégrés analogiques et numériques, microprocesseurs et autres composants etc.) et les marchés sur lesquels ils sont attendus. Son objectif : " faire du chiffre " sans oublier de soigner le relationnel pour ne pas rater un marché.</p>', '[{"nom": "ingénieur technico-commercial / ingénieure technico-commerciale en électronique", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.104"}, {"nom": "Relation technico-commerciale", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=D1407"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_107', 'chargé/e d''affaires en génie climatique', '<p>Ventilations, appareils de surpression d''eau, planchers chauffants, climatisations... pour chaque type de bâtiment, le chargé d''affaires en génie climatique vend la solution adaptée à des entreprises clientes.</p>', '[{"nom": "Ingénierie et études du BTP", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=F1106"}, {"nom": "chargé / chargée d''affaires en génie climatique", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.107"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_106', 'ingénieur / ingénieure hydrogéomorphologue', '<p>L''ingénieur ou l''ingénieure hydrogéomorphologue analyse le fonctionnement naturel des cours d''eau et la structure de leurs vallées afin de cartographier les zones inondables. Il ou elle étudie également l''érosion côtière afin de prévenir d''éventuelles crues.</p>', '[{"nom": "ingénieur / ingénieure hydrogéomorphologue", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.106"}, {"nom": "Information géographique", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=M1808"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_109', 'responsable de laboratoire de recherche', '<p> Un robot à vision autonome ou un traitement médical contre l''ostéoporose sont l''aboutissement final de longues études. Au responsable de laboratoire de recherche de définir, piloter et valider ces travaux d''une équipe de chercheurs et de techniciens. </p>', '[{"nom": "responsable de laboratoire de recherche", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.109"}, {"nom": "Recherche en sciences de l''univers, de la matière et du vivant", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K2402"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_108', 'médiateur / médiatrice scientifique', '<p>Rébarbatives, les sciences ? Le médiateur scientifique démontre chaque jour le contraire. Cet orateur est un bon pédagogue : il sait rendre accessible un savoir complexe à un public néophyte.</p>', '[{"nom": "médiateur / médiatrice scientifique", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.108"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_292', 'formateur / formatrice d''adultes', '<p>Dans des domaines très divers, le formateur d''adultes s''adresse à des publics variés qui veulent mettre à jour leurs connaissances ou acquérir de nouveaux apprentissages, dans le cadre d''une évolution de leur métier.</p>', '[{"nom": "Formation professionnelle", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K2111"}, {"nom": "formateur / formatrice d''adultes", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.292"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_295', 'tailleur / tailleuse de pierre', '<p>Héritier des bâtisseurs de cathédrales, cet ouvrier du bâtiment est un véritable artiste. Dépositaire d''un savoir-faire ancestral et respectueux des traditions, il n''en est pas moins ouvert aux dernières évolutions technologiques.</p>', '[{"nom": "tailleur / tailleuse de pierre", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.295"}, {"nom": "Taille et décoration de pierres", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=F1612"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_294', 'technicien / technicienne en traitement des déchets', '<p>Métal, plastique, verre, papier... Tous ces matériaux sont triés, analysés avant d''être recyclés par la technicienne ou le technicien en traitement des déchets. Chargés de leur redonner une nouvelle vie, ils contribuent au respect de l''environnement.</p>', '[{"nom": "technicien / technicienne en traitement des déchets", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.294"}, {"nom": "Supervision d''exploitation éco-industrielle", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K2306"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_297', 'contrôleur / contrôleuse technique automobile', '<p>Pneus, freins, suspensions, échappement... le contrôleur technique ausculte les véhicules automobiles sous tous les angles. Objectif : pointer les dysfonctionnements par rapport à la réglementation, et conseiller les conducteurs sur les réparations à effectuer.</p>', '[{"nom": "contrôleur / contrôleuse technique automobile", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.297"}, {"nom": "Mécanique automobile et entretien de véhicules", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=I1604"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_296', 'charpentier / charpentière métallique', '<p>Le charpentier ou la charpentière métallique fabrique ou assemble les structures en acier qui forment la charpente des ponts, des immeubles, des pylônes, des bâtiments, etc. Il ou elle travaille en atelier ou sur un chantier. Son savoir-faire est recherché.</p>', '[{"nom": "Réalisation de structures métalliques", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H2911"}, {"nom": "charpentier / charpentière métallique", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.296"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_299', 'chef / cheffe de rayon', '<p>Maillon essentiel de la grande distribution, le chef de rayon gère et anime, avec l''aide de son équipe, un rayon spécialisé. Son objectif : générer du trafic et faire progresser le chiffre d''affaires.</p>', '[{"nom": "Management/gestion de rayon produits alimentaires", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=D1502"}, {"nom": "chef / cheffe de rayon", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.299"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_298', 'installateur / installatrice en télécoms', '<p>L''installateur en télécoms installe les équipements et réseaux télécoms des particuliers (téléphone, télévision, Internet), des entreprises et des administrations. Puis il s''assure de leur bon fonctionnement.</p>', '[{"nom": "Installation et maintenance télécoms et courants faibles", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=I1307"}, {"nom": "installateur / installatrice en télécoms", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.298"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_291', 'tailleur-couturier / tailleuse-couturière', '<p>Le tailleur-couturier modifie, ajuste, retouche ou crée des vêtements sur mesure et personnalisés. Il est doté d''un savoir-faire rigoureux, d''un grand sens de l''esthétique et d''une certaine fibre commerciale pour contenter une clientèle exigeante.</p>', '[{"nom": "tailleur-couturier / tailleuse-couturière", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.291"}, {"nom": "Réalisation de vêtements sur mesure ou en petite série", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=B1803"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_290', 'technicien forestier / technicienne forestière', '<p>Très proche du terrain, le technicien forestier est un véritable gestionnaire de la forêt. Cette profession, aux débouchés restreints, s''adresse à des jeunes motivés par le contact avec la nature.</p>', '[{"nom": "technicien forestier / technicienne forestière", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.290"}, {"nom": "Conseil et assistance technique en agriculture", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=A1301"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_271', 'guichetier / guichetière', '<p>Le guichetier est la première personne à qui l''on a affaire quand on pousse la porte d''une agence bancaire. La qualité de son accueil compte beaucoup pour l''image de l''établissement. Ce poste permet à un débutant de faire ses preuves.</p>', '[{"nom": "guichetier / guichetière", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.271"}, {"nom": "Accueil et services bancaires", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=C1201"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_270', 'agent immobilier / agente immobilière', '<p>Commercial dans l''âme, l''agent immobilier met en relation vendeur et acheteur, ou bailleur et locataire, en vue de vendre ou louer des biens. À la tête d''une petite entreprise, franchisée ou non, il anime une équipe de négociateurs.</p>', '[{"nom": "agent immobilier / agente immobilière", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.270"}, {"nom": "Transaction immobilière", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=C1504"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_273', 'chef / cheffe de fabrication des industries graphiques', '<p>Livrer des imprimés dans les délais, tel est le défi relevé au quotidien par le chef de fabrication des industries graphiques. Ses missions : analyser les commandes de ses clients, organiser les plannings et gérer l''ensemble de la production.</p>', '[{"nom": "Encadrement des industries graphiques", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=E1303"}, {"nom": "chef / cheffe de fabrication des industries graphiques", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.273"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_275', 'économiste de la construction', '<p>Présent en amont et durant tout le chantier, l''économiste de la construction chiffre l''ensemble des coûts, puis vérifie que ceux-ci ne dérapent pas. Il assure également une mission de conseil auprès du maître d''oeuvre, de l''architecte ou du client.</p>', '[{"nom": "économiste de la construction", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.275"}, {"nom": "Métré de la construction", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=F1108"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_274', 'conducteur / conductrice de machine à imprimer', '<p>Préparer la machine en effectuant les réglages, réaliser des essais, lancer l''impression d''étiquettes, de journaux, magazines, livres, affiches ou encore plaquettes... sont autant d''opérations orchestrées par le conducteur de machines à imprimer.</p>', '[{"nom": "Conduite de machines d''impression", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=E1301"}, {"nom": "conducteur / conductrice de machine à imprimer", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.274"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_277', 'enseignant/e d''art', '<p>À travers la découverte des oeuvres et par la pratique, l''enseignant d''art, créatif et pédagogue, éduque l''oeil et la main de ses élèves, développe leur sens artistique et esthétique, et leur communique le goût de la création.</p>', '[{"nom": "enseignant / enseignante d''art", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.277"}, {"nom": "Enseignement artistique", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K2105"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_276', 'orthoprothésiste', '<p>L''orthoprothésiste conçoit et pose des prothèses (pour remplacer un membre) et des orthèses (pour corriger une déficience). Son but : compenser les handicaps et redonner aux patients leur autonomie.</p>', '[{"nom": "Prothèses et orthèses", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=J1411"}, {"nom": "orthoprothésiste", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.276"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_279', 'psychanalyste', '<p>Le psychanalyste est un thérapeute qui aide une personne à mieux vivre grâce à une cure psychanalytique. Inventée par Freud, cette technique permet au patient d''explorer son inconscient pour essayer de résoudre les conflits qui remontent à son enfance et pèsent sur son existence.</p>', '[{"nom": "Psychologie", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K1104"}, {"nom": "psychanalyste", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.279"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_278', 'conseiller principal d''éducation / conseillère principale d''éducation', '<p>Figure incontournable du collège et du lycée, le CPE (conseiller principal d''éducation) seconde le chef d''établissement dans l''organisation de la vie scolaire et assure le lien entre les familles et l''équipe pédagogique.</p>', '[{"nom": "Éducation et surveillance au sein d''établissements d''enseignement", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K2104"}, {"nom": "conseiller principal d''éducation / conseillère principale d''éducation", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.278"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_282', 'podo-orthésiste', '<p>Le podo-orthésiste est un spécialiste de l''appareillage du pied. Il facilite la marche au quotidien, en concevant et en fabriquant des chaussures, des semelles et des prothèses orthopédiques sur mesure.</p>', '[{"nom": "Prothèses et orthèses", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=J1411"}, {"nom": "podo-orthésiste", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.282"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_281', 'opticien-lunetier / opticienne-lunetière', '<p>Commercial, technicien, professionnel de la santé... il cumule les fonctions ! Et peut regarder l''avenir avec confiance : le vieillissement de la population et un meilleur suivi médical lui garantissent une clientèle toujours plus nombreuse.</p>', '[{"nom": "opticien-lunetier / opticienne-lunetière", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.281"}, {"nom": "Optique - lunetterie", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=J1405"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_284', 'chargé / chargée de valorisation de la recherche', '<p>Toujours au courant des dernières découvertes, le chargé de valorisation de la recherche accompagne les chercheurs pour protéger leurs travaux et favoriser leur application dans l''industrie.</p>', '[{"nom": "chargé / chargée de valorisation de la recherche", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.284"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_283', 'gérant / gérante de restauration collective', '<p>Le gérant de restauration collective veille sur la préparation de plusieurs centaines de repas quotidiens, que ce soit pour une école, un hôpital ou une entreprise. Chef d''équipe et gestionnaire, son travail varie selon la taille de son restaurant.</p>', '[{"nom": "gérant / gérante de restauration collective", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.283"}, {"nom": "Management d''établissement de restauration collective", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=G1404"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_286', 'chef / cheffe de chantier en installations électriques', '<p>Intermédiaire entre le bureau d''études et les monteurs électriciens, le chef de chantier supervise le montage d''installations électriques dans les entreprises, les administrations, les logements. Un métier qualifié qui demande de l''expérience.</p>', '[{"nom": "chef / cheffe de chantier en installations électriques", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.286"}, {"nom": "Direction de chantier du BTP", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=F1202"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_285', 'hôte / hôtesse d''accueil', '<p>Premier interlocuteur des visiteurs et des collaborateurs, l''hôte d''accueil représente l''image de l''organisation qui l''emploie ; sa prestation doit donc être irréprochable. Ce professionnel est recherché, surtout s''il maîtrise l''anglais.</p>', '[{"nom": "Accueil et renseignements", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=M1601"}, {"nom": "hôte / hôtesse d''accueil", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.285"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_288', 'technicien / technicienne biologiste', '<p>Recherche publique, hôpitaux, industrie pharmaceutique et agroalimentaire… les secteurs où les biologistes peuvent exercer sont nombreux. Leur rôle : analyser, mettre au point et contrôler les produits.</p>', '[{"nom": "technicien / technicienne biologiste", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.288"}, {"nom": "Intervention technique en études, recherche et développement", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1210"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_287', 'conducteur/trice de machines agricoles', '<p>Tracteur, ensileuse, moissonneuse-batteuse... Autant d''engins pilotés par le conducteur de machines agricoles, pour le compte d''un exploitant ou d''une entreprise de travaux agricoles. Il gère aussi l''entretien courant de ces machines sophistiquées. </p>', '[{"nom": "Conduite d''engins agricoles et forestiers", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=A1101"}, {"nom": "conducteur / conductrice de machines agricoles", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.287"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_280', 'ergothérapeute', '<p>Professionnel/le de santé, l''ergothérapeute intervient auprès des personnes de tous âges ou en situation de handicap, pour faciliter la réalisation de leurs activités, en tenant compte de leurs choix de vie et de leur environnement.</p>', '[{"nom": "Ergothérapie", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=J1403"}, {"nom": "ergothérapeute", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.280"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_289', 'ébéniste', '<p>Fabrication de mobilier contemporain, restauration ou copie d''anciens... l''ébéniste réalise ou répare du mobilier ou des aménagements en bois. Salarié d''une entreprise ou à son compte, il exerce un métier d''art. Talent et motivation indispensables.</p>', '[{"nom": "ébéniste", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.289"}, {"nom": "Réalisation de meubles en bois", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H2207"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_250', 'employé/e d''élevage', '<p>Salarié sur une exploitation agricole spécialisée dans la production animale (bovins, ovins, caprins, porcs, volailles...), l''employé d''élevage concourt à soigner les animaux et veiller au bien-être du troupeau. </p>', '[{"nom": "employé / employée d''élevage", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.250"}, {"nom": "Élevage bovin ou équin", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=A1407"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_253', 'journaliste radio et TV', null, '[]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_257', 'décorateur / décoratrice d''intérieur', '<p>Le décorateur d''intérieur, conçoit des espaces (appartements, restaurants, boutiques...) en respectant désirs de son client et contraintes du lieu qu''il tente d''optimiser. Il allie sens esthétique, connaissance des matériaux et des différents intervenants.</p>', '[{"nom": "Conception - aménagement d''espaces intérieurs", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=F1102"}, {"nom": "décorateur / décoratrice d''intérieur", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.257"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_258', 'architecte d''intérieur', '<p> L''architecte d''intérieur conçoit et aménage des espaces intérieurs fonctionnels, esthétiques et confortables en jouant avec les volumes, la lumière, le mobilier et les matériaux, tout en tenant compte de contraintes techniques et budgétaires.</p>', '[{"nom": "Conception - aménagement d''espaces intérieurs", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=F1102"}, {"nom": "architecte d''intérieur", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.258"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_262', 'cadreur / cadreuse', '<p>Un panoramique réussi, un mouvement de travelling impeccable... c''est l''oeuvre du cadreur, qui assure la prise de vues. Un métier très convoité au cinéma, mais qui est plus accessible en reportage ou sur un plateau de télévision.</p>', '[{"nom": "Image cinématographique et télévisuelle", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=L1505"}, {"nom": "cadreur / cadreuse", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.262"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_261', 'décorateur / décoratrice-scénographe', '<p>Mettre en scène une exposition, réaliser le décor d''une émission ou reconstituer un salon dans un film d''époque... Ces missions ont un point commun : elles sont l''oeuvre du décorateur-scénographe. Ses meilleures armes : créativité et ingéniosité.</p>', '[{"nom": "décorateur / décoratrice-scénographe", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.261"}, {"nom": "Décor et accessoires spectacle", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=L1503"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_242', 'ingénieur/e production en mécanique', '<p>L''ingénieur de production mécanique organise et supervise les opérations de fabrication d''un produit industriel en respectant les contraintes de coûts, de qualité et de délais. Il est au coeur des métiers de la mécanique.</p>', '[{"nom": "Management et ingénierie de production", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H2502"}, {"nom": "ingénieur / ingénieure production en mécanique", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.242"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_264', 'monteur / monteuse en installations thermiques et climatiques', '<p>Grâce à cet expert du chaud et du froid, on se sent bien, hiver comme été, à la maison, au bureau ou dans un centre commercial. Le monteur en installations thermiques et climatiques contribue à l''amélioration de notre confort, mais aussi aux économies d''énergie et au respect de l''environnement.</p>', '[{"nom": "monteur / monteuse en installations thermiques et climatiques", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.264"}, {"nom": "Installation d''équipements sanitaires et thermiques", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=F1603"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_263', 'couvreur / couvreuse', '<p>Grâce au couvreur, le ciel ne nous tombe pas sur la tête ! Spécialiste de la construction et de la réfection des toits, il nous met à l''abri des intempéries (pluie, neige, vent... ou soleil accablant). Un métier incontournable dans le bâtiment.</p>', '[{"nom": "couvreur / couvreuse", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.263"}, {"nom": "Pose et restauration de couvertures", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=F1610"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_266', 'plombier / plombière', '<p>Un robinet qui fuit, un évier bouché, un chauffe-eau en panne... On a toujours besoin d''un plombier ! Le métier requiert une bonne condition physique, mais aussi une capacité à analyser la situation et à choisir la bonne méthode d''intervention.</p>', '[{"nom": "Installation d''équipements sanitaires et thermiques", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=F1603"}, {"nom": "plombier / plombière", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.266"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_265', 'conseiller / conseillère agricole', '<p>Le conseiller agricole accompagne les agriculteurs. À l''heure où l''agriculture se complexifie, il fait figure de partenaire privilégié de l''exploitant. Il l''aide à développer son activité, à choisir de nouveaux équipements et à améliorer la qualité.</p>', '[{"nom": "conseiller / conseillère agricole", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.265"}, {"nom": "Conseil et assistance technique en agriculture", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=A1301"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_268', 'boucher / bouchère', '<p>Spécialiste de la viande, le boucher intervient de l''approvisionnement à la vente au détail. Qu''il travaille à son compte ou dans un supermarché, ce professionnel doit posséder des compétences techniques, le goût du contact et le sens de l''accueil.</p>', '[{"nom": "Boucherie", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=D1101"}, {"nom": "boucher / bouchère", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.268"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_267', 'attaché commercial / attachée commerciale', '<p>Véritable ambassadeur des marques qu''il représente, l''attaché commercial trouve les arguments pour convaincre ses clients d''acheter les produits dont il s''occupe. Son objectif : décrocher de nouveaux marchés pour son entreprise.</p>', '[{"nom": "Relation commerciale grands comptes et entreprises", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=D1402"}, {"nom": "attaché commercial / attachée commerciale", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.267"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_269', 'libraire', '<p>Romans, guides pratiques, manuels scolaires, bandes dessinées… Quel que soit le domaine, le libraire guide et conseille sa clientèle. Plus qu''un simple commerçant, c''est un passionné de livres qui sait faire partager ses coups de coeur !</p>', '[{"nom": "libraire", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.269"}, {"nom": "Vente en articles de sport et loisirs", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=D1211"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_231', 'ingénieur / ingénieure intégration satellite', '<p>Il coordonne et organise les activités d''intégration d''un satellite, notamment en supervisant l''assemblage des différentes parties, puis il teste la résistance de l''engin avant de le lancer sur orbite.</p>', '[{"nom": "Management et ingénierie études, recherche et développement industriel", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1206"}, {"nom": "ingénieur / ingénieure intégration satellite", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.231"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_230', 'statisticien/ne en analyse sensorielle', '<p>Donner des éléments objectifs et quantifiables aux décideurs concernant leurs produits, c''est le rôle du statisticien en analyse sensorielle qui travaille sur les goûts des consommateurs, dans l''agroalimentaire, la cosmétique, l''automobile, etc.</p>', '[{"nom": "statisticien / statisticienne en analyse sensorielle", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.230"}, {"nom": "Études et prospectives socio-économiques", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=M1403"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_233', 'pépiniériste', null, '[]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_232', 'hydrobiologiste', e'L\'hydrobiologiste est spécialiste de la qualité de l\'eau. Depuis les prélèvements en milieu aquatique jusqu\'au laboratoire, en passant par l\'ordinateur pour écrire des rapports, le métier demande de la rigueur et des connaissances scientifiques pointues.<h3>Après le bac</h3>
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_234', 'conseiller/ère d''élevage', '<p>Le conseiller d''élevage apporte son expertise pour optimiser la production. Il conseille l''éleveur sur la gestion et le suivi de ses animaux, pour améliorer la qualité de la production (lait, viande, oeufs, etc.), ainsi que la rentabilité de l''exploitation.</p>', '[{"nom": "conseiller / conseillère d''élevage", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.234"}, {"nom": "Conseil et assistance technique en agriculture", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=A1301"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_239', 'concepteur/trice designer packaging', '<p>Le concepteur designer packaging crée des emballages adaptés à la demande d''un client. Il respecte un cahier des charges détaillant l''ensemble des contraintes appliquées au packaging tout au long de son cycle de vie..</p>', '[{"nom": "concepteur / conceptrice designer packaging", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.239"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_218', 'consultant/e en management de l''innovation', '<p>Véritable guide pour les entreprises qui veulent innover, il les aide dans la mise en oeuvre de solutions technologiques, afin d''améliorer leur rentabilité.</p>', '[{"nom": "Conseil en organisation et management d''entreprise", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=M1402"}, {"nom": "consultant / consultante en management de l''innovation", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.218"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_244', 'ingénieur / ingénieure systèmes embarqués', '<p>L''ingénieur électronique spécialisé en systèmes embarqués conçoit des systèmes complexes pour des objets mobiles et communicants via un réseau internet... à des fins de surveillance, de contrôle, de communication, de santé, de sécurité...</p>', '[{"nom": "ingénieur / ingénieure systèmes embarqués", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.244"}, {"nom": "Management et ingénierie études, recherche et développement industriel", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1206"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_243', 'ingénieur/e en électronique numérique', '<p>Pass transport sans contact, téléphones portables, drones, guidages de missiles... l''ingénieur en électronique numérique conçoit toute une gamme de produits pour le grand public ou les professionnels. Un métier où innovation et efficacité se cultivent au quotidien.</p>', '[{"nom": "Management et ingénierie études, recherche et développement industriel", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1206"}, {"nom": "ingénieur / ingénieure en électronique numérique", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.243"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_246', 'responsable qualité aéronautique', null, '[]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_247', 'Thanatopracteur / thanatopractrice', e'Salariés d\'une entreprise de pompes funèbres, d\'un service municipal ou à leur compte, les thanatopracteurs préparent les corps des défunts avant la mise en cercueil. Cette profession, très réglementée, demande rigueur, tact et endurance.<h3>Après le bac</h3>
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_211', 'ingénieur/e production en aéronautique', '<p>La gestion d''une ligne de production dans le milieu aéronautique demande à la fois de hautes compétences techniques et une grande capacité à manager. Des qualités que l''ingénieur production possède. </p>', '[{"nom": "Management et ingénierie de production", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H2502"}, {"nom": "ingénieur / ingénieure production en aéronautique", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.211"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_210', 'chauffeur-livreur / chauffeuse-livreuse', '<p>Indispensable avec l''explosion du e-commerce, la chauffeuse-livreuse ou le chauffeur-livreur charge son véhicule avant sa tournée qui peut comprendre des dizaines d''arrêts pour livrer ses clients en temps et en heure. Organisation, ponctualité et amabilité sont requises !</p>', '[{"nom": "chauffeur-livreur / chauffeuse-livreuse", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.210"}, {"nom": "Conduite et livraison par tournées sur courte distance", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=N4105"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_213', 'gestionnaire de patrimoine', '<p>Constamment à la recherche des solutions les mieux adaptées aux attentes de ses clients, le gestionnaire de patrimoine, est un spécialiste de l''investissement sur mesure et un expert en ingénierie patrimoniale qui possède de solides connaissances en économie, finance, fiscalité et droit</p>', '[{"nom": "Conseil en gestion de patrimoine financier", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=C1205"}, {"nom": "gestionnaire de patrimoine", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.213"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_212', 'consultant/e en systèmes d''information', '<p>Expert technique, le consultant en systèmes d''information intervient à la demande d''une entreprise pour améliorer ses systèmes d''information : serveurs, bases de données, logiciels de gestion...</p>', '[{"nom": "consultant / consultante en systèmes d''information", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.212"}, {"nom": "Conseil et maîtrise d''ouvrage en systèmes d''information", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=M1806"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_215', 'chargé/e de pharmacovigilance', '<p>Le chargé de pharmacovigilance contrôle la tolérance des médicaments fabriqués et commercialisés par son laboratoire pharmaceutique. Il analyse le moindre effet indésirable.</p>', '[{"nom": "chargé / chargée de pharmacovigilance", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.215"}, {"nom": "Management et ingénierie qualité industrielle", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1502"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_214', 'agent / agente artistique', '<p>Comédien, chanteur, mannequin, écrivain... Tous ces artistes se reposent sur une seule et même personne : leur agent. Intermédiaire digne de confiance, il ou elle prend en charge leur carrière de A à Z. Avec un objectif : la faire décoller !</p>', '[{"nom": "Promotion d''artistes et de spectacles", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=L1303"}, {"nom": "agent / agente artistique", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.214"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_217', 'développeur/euse multimédia', e'La développeuse ou le développeur multimédia écrit du code à l\'aide d\'un langage de programmation et conçoit les algorithmes qui permettront de construire un produit multimédia abouti au niveau technique. Il ou elle travaille au sein d\'une équipe pluridisciplinaire et doit être à l\'écoute des autres.<h3>Après le bac</h3>
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_216', 'infirmier/ère anesthésiste', null, '[]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_219', 'chef / cheffe de mission humanitaire', '<p>C''est le capitaine d''une mission humanitaire. Il suit de près l''évolution de la situation pour orienter au mieux les actions de terrain. En relation constante avec son ONG (organisation non gouvernementale), il détermine la stratégie de la mission, tout en dirigeant une équipe. </p>', '[{"nom": "chef / cheffe de mission humanitaire", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.219"}, {"nom": "Conception et organisation de la chaîne logistique", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=N1301"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_221', 'ostéopathe', '<p>Un membre bloqué ? Ce praticien peut vous soulager. L''ostéopathe est qualifié dans la manipulation des muscles et des articulations osseuses. Il pratique exclusivement avec les mains pour traiter certains problèmes mécaniques du corps humain.</p>', '[{"nom": "ostéopathe", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.221"}, {"nom": "Ostéopathie et chiropraxie", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=J1408"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_224', 'ingénieur/e forage', '<p>Essentiel à l''industrie pétrolière, l''ingénieur forage prépare et suit les opérations de percement sous-marins ou souterrain. Cet ingénieur spécialisé, dont la carrière se déroule en grande partie à l''étranger, est très recherché.</p>', '[{"nom": "ingénieur / ingénieure forage", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.224"}, {"nom": "Direction et ingénierie d''exploitation de gisements et de carrières", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=F1203"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_223', 'climatologue', '<p>La qualité de l''eau ou de l''air comme le réchauffement climatique sont en tête des préoccupations. Les climatologues sont des chercheurs qui mènent des études sur le long terme pour prévoir les évolutions de notre climat et les conséquences possibles.</p>', '[{"nom": "Recherche en sciences de l''univers, de la matière et du vivant", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K2402"}, {"nom": "climatologue", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.223"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_226', 'chef/fe d''exploitation d''usine d''incinération', '<p>Incinérer les déchets consiste à les brûler à très haute température (plus de 1 000°C). La cheffe ou le chef d''exploitation d''usine d''incinération fait en sorte que tout se passe bien durant cette opération et garantit la conformité du procédé.</p>', '[{"nom": "Supervision d''exploitation éco-industrielle", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=K2306"}, {"nom": "chef / cheffe d''exploitation d''usine d''incinération", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.226"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_225', 'ingénieur / ingénieure analyste de l''air', '<p>L''ingénieure ou l''ingénieur analyste de l''air a un rôle de sentinelle : il ou elle surveille les particules que nous respirons. Quand une pollution importante est détectée, il lui faut informer les pouvoirs publics et proposer des solutions pour améliorer la qualité de l''air.</p>', '[{"nom": "ingénieur / ingénieure analyste de l''air", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.225"}, {"nom": "Direction de laboratoire d''analyse industrielle", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1501"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_228', 'ingénieur / ingénieure cloud computing', '<p>Pour les nouveaux besoins des entreprises en termes d''accessibilité de leur système d''information, l''ingénieur cloud computing travaille sur le stockage des données en dehors de l''entreprise et à leur accès sécurisé depuis mobiles, tablettes ou postes de travail.</p>', '[{"nom": "ingénieur / ingénieure cloud computing", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.228"}, {"nom": "Expertise et support en systèmes d''information", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=M1802"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_227', 'animateur / animatrice de bassin versant', '<p>L''animatrice ou l''animateur de bassin versant sensibilise à la protection du milieu aquatique local. Ils contribuent à la conservation des ressources en eau et à leur qualité en impliquant les agriculteurs dans une démarche d''amélioration continue.</p>', '[{"nom": "Ingénierie en agriculture et environnement naturel", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=A1303"}, {"nom": "animateur / animatrice de bassin versant", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.227"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_229', 'chef/fe de produit technique en informatique', '<p>Le chef de produit technique en informatique gère la vie d''un produit, de sa conception à sa commercialisation, avec les équipes techniques et commerciales de l''entreprise. Il allie compétences techniques et marketing pour ce poste transversal.</p>', '[{"nom": "Management et gestion de produit", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=M1703"}, {"nom": "chef / cheffe de produit technique en informatique", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.229"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_200', 'maraîcher/ère', '<p>Sans lui, pas de beaux légumes frais dans l''assiette. Le maraîcher cultive les légumes en plein champ ou sous serre. Un métier en contact avec la nature, moins physique grâce à la mécanisation, mais qui nécessite toujours rigueur et attention.</p>', '[{"nom": "Horticulture et maraîchage", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=A1414"}, {"nom": "maraîcher / maraîchère", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.200"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_209', 'chargé /chargée de recherche en recrutement', '<p>Le chargé de recherche en recrutement identifie et présélectionne des candidats qui correspondent au profil établi par le responsable de recrutement. Il travaille en amont du recrutement proprement dit, le plus souvent en cabinet spécialisé.</p>', '[{"nom": "Développement des ressources humaines", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=M1502"}, {"nom": "chargé /chargée de recherche en recrutement", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.209"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_202', 'responsable assurance qualité', '<p> Améliorer la qualité des produits et des services de l''entreprise est la mission du responsable assurance qualité. Un rôle essentiel pour l''image et la compétitivité de son employeur, mais qui demande l''adhésion de tous : de l''ouvrier jusqu''au PDG (président directeur général).</p>', '[{"nom": "Management et ingénierie qualité industrielle", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1502"}, {"nom": "responsable assurance qualité", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.202"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_201', 'bio-informaticien / bio-informaticienne', '<p>Le bio-informaticien met l''univers normalisé de l''informatique au service du monde très mouvant des sciences du vivant. Ce qui exige une véritable double compétence.</p>', '[{"nom": "Management et ingénierie études, recherche et développement industriel", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1206"}, {"nom": "bio-informaticien / bio-informaticienne", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.201"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_204', 'ingénieur/e technico-commercial/e en informatique', '<p>L''ingénieur technico-commercial en informatique est le champion de la solution informatique sur mesure pour les entreprises et les administrations. Son rôle : accompagner un projet, de sa négociation commerciale à sa réalisation technique.</p>', '[{"nom": "Relation technico-commerciale", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=D1407"}, {"nom": "ingénieur technico-commercial / ingénieure technico-commerciale en informatique", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.204"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_203', 'chef / cheffe de projet informatique', '<p>Véritable chef d''orchestre, le chef de projet informatique traduit les demandes de son client en solutions informatiques. De l''analyse des besoins à la livraison du produit, ses missions exigent des compétences aussi bien techniques que managériales.</p>', '[{"nom": "Direction des systèmes d''information", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=M1803"}, {"nom": "chef / cheffe de projet informatique", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.203"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_206', 'ingénieur / ingénieure calcul', '<p>Frottements, température, pression, à l''aide de logiciels pointus de calcul, l''ingénieur calcul étudie la résistance des matériaux et structures. Il peut exercer dans l''aéronautique, la prospection pétrolière, la construction ferroviaire, urbaine...</p>', '[{"nom": "Management et ingénierie études, recherche et développement industriel", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1206"}, {"nom": "ingénieur / ingénieure calcul", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.206"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_205', 'technicien / technicienne radioprotection', '<p>Sur site ou chantier, le technicien en radioprotection joue un rôle-clé en analysant chaque environnement et situation afin de mettre en place des mesures de prévention et de limite des risques de rayonnement.</p>', '[{"nom": "technicien / technicienne radioprotection", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.205"}, {"nom": "Intervention technique en Hygiène Sécurité Environnement -HSE- industriel", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1303"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_208', 'ingénieur / ingénieure d''application', '<p> Dans le secteur de la santé, l''ingénieur d''application travaille pour un fabricant industriel de matériels médicaux (scanners, échographes, etc.). Il assure la promotion d''une gamme de produits et forme les utilisateurs. </p>', '[{"nom": "Management et ingénierie études, recherche et développement industriel", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H1206"}, {"nom": "ingénieur / ingénieure d''application", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.208"}]');
INSERT INTO ref_metier (id, label, descriptif_general, liens) VALUES ('MET_207', 'responsable de production alimentaire', '<p>Le responsable de production alimentaire a en charge le suivi de la fabrication industrielle et du conditionnement des produits alimentaires de son usine : légumes en boîtes de conserve ou surgelés, plats préparés, pâtisseries, yaourts, boissons...</p>', '[{"nom": "Encadrement d''équipe en industrie de transformation", "url": "https://candidat.pole-emploi.fr/marche-du-travail/fichemetierrome?codeRome=H2504"}, {"nom": "responsable de production alimentaire", "url": "https://explorer-avenirs.onisep.fr/http/redirection/metier/slug/MET.207"}]');
